import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const ProfileVisited = () => {

    const [user, setUser] = useState(null);
    const [pastEvents, setPastEvents] = useState([]);
    const [futureEvents, setFutureEvents] = useState([]);


    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        const queryParams = new URLSearchParams(location.search);
        const userId = queryParams.get('id');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verifyOthers', { headers: { authorization: token }, params: { userId: userId } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user);
                        fetchPastEvents(userId);
                        fetchFutureEvents(userId);
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    const fetchPastEvents = async (userId) => {
        await axios.get('/api/events/pastEvents', { headers: { authorization: sessionStorage.getItem('token') }, params: { userId: userId } })
            .then(response => {
                setPastEvents(response.data || []);
            })
            .catch(error => {
                console.error('Error fetching events:', error);
            });
    }

    const fetchFutureEvents = async (userId) => {
        await axios.get('/api/events/futureEvents', { headers: { authorization: sessionStorage.getItem('token') }, params: { userId: userId } })
            .then(response => {
                setFutureEvents(response.data || []);
            })
            .catch(error => {
                console.error('Error fetching events:', error);
            });
    }

    const handleEventClick = (eventId) => {
        const goto = '/evento?id=' + eventId;
        navigate(goto);
    }

    if (!user) {
        return <div>Caricamento in corso...</div>
    }

    return (
        <div className='fullpage'>
            <Navbar />
            <div className='row'> 
                <div className='col-4'></div>

                <div className='col-4'>
                    <div className='bg-white futureEvents ps-5 p-4 m-2 bg-white rounded-3 text-center shadow-lg'>
                        <h1 className='text-primary fw-bolder'>{user.username}</h1>

                    </div>
                </div>

                <div className='col-4'></div>

            </div>
            <div className="row ">

                <div className='col-6 '>
                    <div className=' m-5 futureEvents p-4 p-lg-5 bg-white rounded-3 text-center shadow-lg'>
                        <h1 className='text-primary fw-bolder mb-3'>Ha partecipato:</h1>
                        <div className='row gx-lg-5 '>
                            {pastEvents.length > 0 ? (
                                pastEvents.map((event) => {
                                    const data = new Date(event.date);
                                    return (
                                        <div key={event._id} className="event card col-6 col-xxl-6 col-sm-12 mb-5  border-0 bg-transparent text-start " onClick={() => handleEventClick(event._id)}>
                                            <figure className="card-img-top mb-0 overflow-hidden bsb-overlay-hover">
                                                <a className='overflow-hidden'>
                                                    {event.images.length > 0 && (
                                                        <img src={`${event.images[0]}`} alt={`event`} className="img-thumbnail" />
                                                    )
                                                    }{event.images.length <= 0 && (
                                                        <img src='/assets/img/logoNuovo.png' alt={`event`} className="img-thumbnail" />
                                                    )}
                                                </a>
                                                <figcaption>
