import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';


const EventDetails = () => {
    const token = sessionStorage.getItem('token');
    const [event, setEvent] = useState(null);
    const [user, setUser] = useState(null);
    const [comments, setComments] = useState([]);
    const [error, setError] = useState('');
    const [formData, setFormData] = useState({
        text: '',
        user: '',
        event: '',
        data: new Date()
    });
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        //Prendo parametro id dall'url 
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        if (eventId) {
            axios.get(`/api/events/${eventId}`, { headers: { authorization: sessionStorage.getItem('token') } })
                .then(response => {
                    setEvent(response.data);
                    fetchEventComments(eventId);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    console.error(error);
                });

            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/homepage');
                    } else {
                        setUser(response.data.user);
                    }
                })
                .catch(error => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
        } else {
            setError('Event ID not found');
        }
    }, [location]);

    const fetchEventComments = async (eventId) => {
        try {
            const response = await axios.get(`/api/events/commentsOf`, { headers: { eventId } });
            setComments(response.data); 
            console.log('Comments fetched:', response.data);
        } catch (error) {
            setError('Failed to load comments');
            console.error(error);
        }
    };


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const queryParams = new URLSearchParams(location.search);

        try {
            const comment = {
                text: formData.text,
                user: user._id, 
                event: queryParams.get('id'), 
                data: new Date()
            };

            const response = await axios.post('/api/events/addComment', comment);

            if (response.status === 201) {
                window.location.reload();
            } else {
                console.error('Errore durante l\'aggiunta del commento:', response);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    if (error) {
        return <div>{error}</div>;
    }

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div className="fullpage">
            <Navbar />

            <div className=" container px-lg-5">
                <div className="futureEvents p-4 p-lg-5 bg-white rounded-3 text-center" >
                    <div className="m-4 m-lg-4">
                        <h1 className="display-5 fw-bold text-primary">{event.name}</h1>
                    </div>
                    <div className='row'>

                        <div className="event-details col-lg-6 col-sm-12">
                            <div className='eventDetail'>
                                <img className="img-fluid bsb-scale center bsb-hover-scale-up shadow" loading="lazy" src="https://picsum.photos/1500/1000" alt="Living" />
                            </div>
                        </div>
                        <div className='col-lg-6 col-sm-12 mt-3 '>
                            <div className="event-details text-start text-secondary ">
                                {(event.description === '') ? (<div></div>) : (<h5>{event.description}</h5>)}
                                <h5 className='text-capitalize'><strong>Categoria:</strong> {event.category}</h5>
                                <h5><strong>Data:</strong> {new Date(event.date).toLocaleDateString()}</h5>
                                <h5><strong>Ora:</strong> {new Date(event.date).toLocaleTimeString()}</h5>
                                <h5><strong>Luogo:</strong> {event.location}</h5>
                            </div>
                            <form onSubmit={handleSubmit} className='' >
                                <div className="col-12 mt-3 " >
                                    <div className="form-floating mb-3 ">
                                        <textarea
                                            className="form-control border-primary inserisciCommento"
                                            name="text" 
                                            id="text"
                                            placeholder="Commento..."
                                            rows="3"
                                            value={formData.text}
                                            onChange={handleChange}
                                        ></textarea>
                                        <label htmlFor="text" className="form-label text-secondary">
                                            Commento...
                                        </label>
                                    </div>
                                    <div className="d-flex justify-content-end">
                                        <button type="submit" className="btn btn-danger">
                                            Send
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>

                    <section>
                        <h4 className="m-5 mb-0 text-danger fw-bold">Commenti</h4>
                        <div className="container my-5 mt-0 py-5">
                            <div className="row d-flex justify-content-center">
                                <div className="col-md-12 col-lg-10">
                                    <div className="card text-body shadow">
                                        <div className="card-body p-4">
                                            {console.log(comments) }{comments.comments.length > 0 ? (
                                                <div className="d-flex flex-start">
                                                    <div>
                                                        <div className="d-flex mb-0 row">
                                                            <h6 className=" text-danger fw-bold mb-3 col-6 text-end">Maggie Marsh</h6>
                                                            <span className="mb-3 col-6 text-end text-warning">
                                                                March 07, 2021
                                                            </span>

                                                        </div>
                                                        <p className="mb-0">
                                                            Lorem Ipsum is simply dummy text of the printing and typesetting
                                                            industry. Lorem Ipsum has been the industry's standard dummy text ever
                                                            since the 1500s, when an unknown printer took a galley of type and
                                                            scrambled it.
                                                        </p>
                                                    </div>
                                                </div>
                                            ) : (
                                            <div>
                                                <h6 className='text-black'> Ancora nessun commento</h6>
                                            </div>
                                            )}

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default EventDetails;
