import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';
await google.maps.importLibrary("maps");

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

// Sostituisci con il tuo ID mappa
const mapId = '6e674a5f15d5b512';

function Map() {
    const [markers, setMarkers] = useState([]);
    const mapRef = useRef(null);

    useEffect(() => {
        // Carica i dati dei marker dal tuo DB
        axios.get('/api/events/markers')  // Sostituisci con l'endpoint corretto
            .then(response => {
                setMarkers(response.data);
                console.log('Fetched markers:', response.data);  // Verifica i dati dei marker
            })
            .catch(error => {
                console.error('Error fetching marker data:', error);
            });

        // Carica lo script delle API di Google Maps manualmente
        const loadGoogleMapsScript = () => {
            const script = document.createElement('script');
            script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs&libraries=marker&v=weekly`;
            script.async = true;
            script.onload = () => {
                if (window.google && window.google.maps) {
                    console.log('Google Maps API loaded.');
                } else {
                    console.error('Google Maps API failed to load.');
                }
            };
            document.head.appendChild(script);
        };

        loadGoogleMapsScript();
    }, []);

    const onMapLoad = (mapInstance) => {
        console.log('Map loaded:', mapInstance);
        mapRef.current = mapInstance;

        if (window.google && window.google.maps && window.google.maps.marker) {
            console.log('Google Maps API loaded.');
            markers.forEach(marker => {
                new window.google.maps.marker.AdvancedMarkerElement({
                    map: mapRef.current,
                    position: { lat: marker.latitude, lng: marker.longitude },
                    content: `<div style="color: red;">${marker.name}</div>`
                });
                console.log('Marker added:', marker);
            });
        } else {
            console.error('Google Maps API is not loaded or AdvancedMarkerElement is not available.');
        }
    };

    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow map' id='map'>
                    <div id="map" style={mapContainerStyle}></div>
                    <script async defer src={`https://maps.googleapis.com/maps/api/js?key=AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs&libraries=marker&v=weekly`}></script>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Map;
