import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './App.css';
import Navbar from './Navbar';
import LocationSearchInput from './LocationSearchInput';

function CreateEvent() {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        date: null,
        location: '',
        category: 'cultura',
        images: [],
        latitude: '',
        longitude: '',
    });

    const [user, setUser] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verifyOrganizer', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/organizzatore');
                    } else {
                        setUser(response.data.user);
                    }
                })
                .catch(error => {
                    console.error('Verification error:', error);
                    navigate('/organizzatore');
                });
        }
    }, [navigate]);

    useEffect(() => {
        if (user) {
            setFormData(prevData => ({
                ...prevData,
                organizer: user._id,
            }));
        }
    }, [user]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        const imageUrls = files.map(file => URL.createObjectURL(file));

        setFormData(prevData => ({
            ...prevData,
            images: imageUrls,
        }));
    };

    const handleLocationSelect = (coordinates) => {
        setFormData(prevData => ({
            ...prevData,
            location: coordinates.name,
            latitude: coordinates.latitude,
            longitude: coordinates.longitude,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const event = {
                ...formData,
                organizer: user._id,
            };
            console.log(event);
            const response = await axios.post('/api/event/create', event);
            navigate('/organizzatore');
        } catch (error) {
            console.error('Error:', error);
            setErrorMessage('Errore nella creazione dell\'evento.');
        }
    };

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div className="Create fullpage">
            <Navbar />
            <div className="row justify-content-center">
                <div className="col-12 col-sm-10 col-md-8 col-lg-6 col-xl-5 col-xxl-4">
                    <div className="card border border-light-subtle rounded-3 shadow-sm">
                        <div className="card-body p-3 p-md-4 p-xl-5">
                            <h2 className="fs-1 fw-bold text-center text-danger mb-4">
                                Crea un nuovo evento
                            </h2>
                            <form onSubmit={handleSubmit}>
                                {/* Campi del form come prima */}
                                <div className="col-12">
                                    <div className="mb-3">
                                        <label htmlFor="place" className="form-label text-warning">
                                            Luogo : &ensp;
                                        </label>
                                        <LocationSearchInput onSelect={handleLocationSelect} />
                                    </div>
                                </div>
                                {/* Resto del form */}
                                <div className="col-12">
                                    <div className="d-grid my-3">
                                        <button className="btn btn-danger btn-lg" type="submit">
                                            Crea Evento
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CreateEvent;
