import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'
import './App.css'

function Organizer() {

    const [user, setUser] = useState(null);
    const [events, setEvents] = useState(null);

    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/homepage');
        } else {
            axios.get('/api/auth/verifyOrganizer', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/homepage');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
            axios.get('/api/events/create')
                .then(response => {
                        setEvents(response.data.events) // Imposta l'utente

                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
        }
    }, [navigate]);


    const fetchUserEvents = () => {
      axios.get('/api/events/my-events', { headers: { authorization: localStorage.getItem('token') } })
          .then(response => {
              setEvents(response.data.events);
          })
          .catch(error => {
              console.error('Error fetching events:', error);
          });
  };


    const [formData, setFormData] = useState({ name: '', description : '', date: null, category: 0 });
    const [errorMessage, setErrorMessage] = useState('');

    //Cambia il contenuto di formData in tempo reale mentre si scrive
    const handleChange = (e) => {
      const { name, value, type, checked } = e.target;
      setFormData((prevData) => ({
        ...prevData,
        [name]: type === 'checkbox' ? checked : value,
      }));
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
          const response = await axios.post('/organizzatore', formData);
          localStorage.setItem('token', response.data.token); // Salva il token
          navigate('/homepage');
      } catch (error) {
          console.error('Error:', error);
          setErrorMessage('Invalid login credentials.');
      } 
    };

    if(!user){
        return <div>Caricamento in corso...</div>
    }

    return (

        <div className="Organizer ">

            <Navbar />


            <h1>I tuoi eventi</h1>

            {events.length > 0 ? (
                <ul>
                    {events.map(event => (
                        <li key={event._id}>{event.name} - {event.date}</li>
                    ))}
                </ul>
            ) : (
                <p>Nessun evento creato</p>
            )}


            {/* ------------------------------------ FORM CREA EVENTO --------------------------------------------- */}

            <form onSubmit={handleSubmit}>
                  <div className="row gy-2 overflow-hidden">
                    <div className="col-12">
                      <div className="form-floating mb-3">

                      </div>
                    </div>
                    <div className="col-12">
                      <div className="form-floating mb-3">

                      </div>
                    </div>
                    {errorMessage && (
                      <div className="col-12">
                        <p className="text-danger text-center">{errorMessage}</p>
                      </div>
                    )}

                    <div className="col-12">
                      <div className="d-grid my-3">
                        <button className="btn btn-primary btn-lg" type="submit">
                          Crea
                        </button>
                      </div>
                    </div>
                    <div className="col-12">
                      ù
                    </div>
                  </div>
                </form>    
        </div>


    );
}

export default Organizer;
