import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const Organizer = () => {

    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user);
                        fetchPastEvents(response.data.user._id);
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    const fetchPastEvents = async (userId) => {
        await axios.get('/api/events/pastEvents', { headers: { authorization: sessionStorage.getItem('token') }, params: { userId } })
            .then(response => {
                setEvents(response.data || []);
            })
            .catch(error => {
                console.error('Error fetching events:', error);
            });
    }

    const handleEventClick = (eventId) => {
        navigate(`/evento?id=${eventId}`);
    }

    const handleDeleteUser = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.delete(`/api/auth/delete`, {
                headers: { authorization: sessionStorage.getItem('token') },
                params: { userId: user._id }
            });

            if (response.status === 200) {
                alert("Account eliminato con successo.");
                sessionStorage.removeItem('token');
                navigate('/register');
            } else {
                console.error('Errore durante l\'eliminazione:', response);
                alert('Impossibile eliminare l\'account.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Errore nella richiesta di eliminazione.');
        }
    };

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div className="d-flex flex-column min-vh-100 bg-dark text-white">
            <Navbar />

            <header className="py-5 flex-grow-1">
                <div className="container text-center">
                    <h1 className="display-4 text-warning text-capitalize">I tuoi eventi</h1>
                </div>
                <div className="container mt-5 text-center">
                    <a className="btn btn-primary btn-sm mx-auto mb-4" href='/organizzatore/crea'>
                        Crea
                    </a>
                    <div className="row justify-content-center">
                        {events.length > 0 ? (
                            events.map((event) => {
                                const data = new Date(event.date);
                                return (
                                    <div
                                        key={event._id}
                                        className="col-lg-4 col-md-6 mb-4 d-flex justify-content-center"
                                        onClick={() => handleEventClick(event._id)}
                                    >
                                        <div className="card bg-black text-light h-100">
                                            <figure className="card-img-top mb-0 overflow-hidden">
                                                <img
                                                    src={
                                                        event.images.length > 0
                                                            ? event.images[0]
                                                            : '/assets/img/logoNuovo.png'
                                                    }
                                                    className="w-100"
                                                    alt={event.name}
                                                    style={{ height: '200px', objectFit: 'cover' }}
                                                />
                                            </figure>
                                            <div className="card-body">
                                                <h5 className="card-title text-warning">{event.name}</h5>
                                                <p>
                                                    <span className="badge bg-info">{event.category}</span>
                                                </p>
                                                <p className="text-warning">
                                                    {data.toLocaleDateString('it-IT', {
                                                        year: 'numeric',
                                                        month: 'numeric',
                                                        day: 'numeric',
                                                    })}
                                                    ,{' '}
                                                    {data.toLocaleTimeString('it-IT', {
                                                        hour: '2-digit',
                                                        minute: '2-digit',
                                                    })}
                                                </p>
                                            </div>
                                        </div>
                                    </div>
                                );
                            })
                        ) : (
                            <div className="text-center">
                                <p className="text-muted">Nessun evento creato</p>
                            </div>
                        )}
                    </div>
                </div>
            </header>

            <Footer />
        </div>
    );
}

export default Organizer;
