import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useNavigate, NavLink } from 'react-router-dom';
import getSocket from './Socket';
import debounce from 'lodash.debounce'; // Usare lodash per implementare debounce

function Navbar() {
  const [user, setUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const searchRef = useRef(null);
  const navigate = useNavigate();
  const socket = getSocket();

  useEffect(() => {
    const token = sessionStorage.getItem('token');
    if (!token) {
      setUser(null);
    } else {
      axios.get('/api/auth/verify', { headers: { authorization: token } })
        .then(response => {
          if (response.status !== 200) {
            setUser(null);
            navigate('/login');
          } else {
            setUser(response.data.user);
          }
        })
        .catch(error => {
          console.error('Verification error:', error);
          setUser(null);
          navigate('/login');
        });
    }

    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setDropdownVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [navigate]);

  const logout = () => {
    sessionStorage.removeItem('token');
    if (socket) {
      socket.disconnect();
    }
    navigate('/login');
  };

  const handleSearch = async (term) => {
    if (!term) {
      setSearchResults([]);
      setDropdownVisible(false);
      return;
    }

    try {
      const response = await axios.get('/api/events/search', {
        params: { search: term }
      });
      setSearchResults(response.data);
      setDropdownVisible(true);
    } catch (error) {
      console.error('Search error:', error);
      setDropdownVisible(false);
    }
  };

  const debouncedSearch = useRef(debounce((term) => handleSearch(term), 300)).current;

  const handleInputChange = (event) => {
    const term = event.target.value;
    setSearchTerm(term);

    // Remove the direct call to handleSearch, debounce handles it
    debouncedSearch(term);  // Only trigger search when user has stopped typing
};

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top shadow">
        <div className="container-fluid">
          <NavLink className="navbar-brand" to="/homepage">
            <img src="/assets/img/logoNuovo.png" alt="Logo" className="d-inline-block align-text-top" style={{ width: '50px', height: '50px' }} />
          </NavLink>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <NavLink to="/homepage" className="nav-link">
                  Home
                </NavLink>
              </li>
              <li className="nav-item dropdown">
                <NavLink to="/categorie" className="nav-link dropdown-toggle" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                  Categorie
                </NavLink>
                <ul className="dropdown-menu">
                  <li><NavLink to="/categorie?c=tutto" className="dropdown-item">Tutte</NavLink></li>
                  <li><NavLink to="/categorie?c=cultura" className="dropdown-item">Cultura</NavLink></li>
                  <li><NavLink to="/categorie?c=concerti" className="dropdown-item">Concerti</NavLink></li>
                  <li><NavLink to="/categorie?c=mostre" className="dropdown-item">Mostre</NavLink></li>
                  <li><NavLink to="/categorie?c=party" className="dropdown-item">Party</NavLink></li>
                  <li><NavLink to="/categorie?c=sport" className="dropdown-item">Sport</NavLink></li>
                </ul>
              </li>
              <li className="nav-item">
                <NavLink to="/mappa" className="nav-link">
                  Mappa
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to="/profilo" className="nav-link">
                  Profilo
                </NavLink>
              </li>
              {user && (
                <li className="nav-item">
                  <NavLink to="/organizzatore" className={`nav-link ${user.organizer ? '' : 'disabled'}`} aria-disabled={!user.organizer}>
                    Organizzatore
                  </NavLink>
                </li>
              )}
            </ul>
            <form className="d-flex position-relative" role="search" ref={searchRef}>
              <input
                className="form-control me-2"
                type="search"
                placeholder="Search"
                aria-label="Search"
                value={searchTerm}
                onChange={handleInputChange}
              />
              {dropdownVisible && (
                <ul className="dropdown-menu position-absolute w-100 list-unstyled bg-light rounded">
                  {searchResults.length > 0 ? (
                    searchResults.map((event, index) => (
                      <li key={index}>
                        <a className="dropdown-item text-dark fw-bold" href={`/evento?id=${event._id}`}>
                          {event.name}
                        </a>
                      </li>
                    ))
                  ) : (
                    <li>
                      <span className="dropdown-item text-muted">Nessun risultato</span>
                    </li>
                  )}
                </ul>
              )}
            </form>
            <ul className="navbar-nav mb-2 mt-2 mb-lg-0 align-items-lg-center">
              {user ? (
                <li className="nav-item">
                  <button onClick={logout} className="btn btn-danger ms-lg-5">
                    LogOut
                  </button>
                </li>
              ) : (
                <li className="nav-item">
                  <NavLink to="/login" className="btn btn-primary ms-lg-5">
                    LogIn
                  </NavLink>
                </li>
              )}
            </ul>
          </div>
        </div>
      </nav>
      <div className="m-5 p-2"></div>
    </div>
  );
}

export default Navbar;
