import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import Navbar from './Navbar';
import Footer from './Footer';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

function Map() {
    const [map, setMap] = useState(null);

    useEffect(() => {
        if (map) {
            // Puoi usare l'istanza della mappa qui, se necessario
            // Ad esempio, aggiungi marker, listener, ecc.
        }
    }, [map]);

    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow map'>
                    <LoadScript googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs">
                        <GoogleMap
                            mapContainerStyle={mapContainerStyle}
                            center={center}
                            zoom={15}
                            onLoad={mapInstance => setMap(mapInstance)}  // Imposta l'istanza della mappa
                        >
                            <Marker
                                position={center}
                                icon={{
                                    url: 'https://cdn-icons-png.freepik.com/512/2886/2886372.png',
                                    scaledSize: new window.google.maps.Size(50, 50),
                                }}
                            />
                        </GoogleMap>
                    </LoadScript>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Map;
