import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

function Homepage() {
  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    const token = sessionStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      axios
        .get('/api/auth/verify', { headers: { authorization: token } })
        .then((response) => {
          if (response.status !== 200) {
            navigate('/login');
          } else {
            setUser(response.data.user);
            fetchFutureEvents(response.data.user);
          }
        })
        .catch((error) => {
          console.error('Verification error:', error);
          navigate('/login');
        });
    }
  }, [navigate]);

  const fetchFutureEvents = async (userId) => {
    await axios
      .get('/api/events/futureEvents', {
        headers: { authorization: sessionStorage.getItem('token') },
        params: { userId: userId },
      })
      .then((response) => {
        setEvents(response.data || []);
      })
      .catch((error) => {
        console.error('Error fetching events:', error);
      });
  };

  const handleEventClick = (eventId) => {
    navigate(`/evento?id=${eventId}`);
  };

  if (!user) {
    return <div>Caricamento in corso...</div>;
  }

  return (
    <div className="d-flex flex-column min-vh-100 bg-dark text-white">
      <Navbar />

      <div className="container text-center mt-5">
        <h1 className="display-4 text-warning">Ciao {user.username}!</h1>
      </div>

      <div className="container mt-5">
        <h2 className="text-center text-warning">I tuoi eventi futuri</h2>
        <div className="row justify-content-center">
          {events.length > 0 ? (
            events.map((event) => {
              const data = new Date(event.date);
              return (
                <div
                  key={event._id}
                  className="col-lg-4 col-md-6 mb-4"
                  onClick={() => handleEventClick(event._id)}
                >
                  <div className="card bg-white text-light h-100">
                  <figure className="card-img-top mb-0 overflow-hidden bsb-overlay-hover">
                    <img
                      src={
                        event.images.length > 0
                          ? event.images[0]
                          : '/assets/img/logoNuovo.png'
                      }
                      className=""
                      alt={event.name}
                      style={{ height: '200px', objectFit: 'cover' }}
                    />

                    </figure>


                    <div className="card-body d-flex flex-column">
                      <h5 className="card-title text-warning">{event.name}</h5>
                      <p className="card-text flex-grow-1">
                        <span className="badge bg-info">{event.category}</span>
                      </p>
                      <p className="text-end text-warning">
                        {data.toLocaleDateString('it-IT', {
                          year: 'numeric',
                          month: 'numeric',
                          day: 'numeric',
                        })}
                        ,{' '}
                        {data.toLocaleTimeString('it-IT', {
                          hour: '2-digit',
                          minute: '2-digit',
                        })}
                      </p>
                    </div>
                    <div className="card-footer text-center">
                      <button className="btn btn-warning">
                        Scopri evento
                      </button>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="col-12">
              <p className="text-center text-muted">
                Nessun evento in programma
              </p>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default Homepage;
