import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const EventDetails = () => {
    const [event, setEvent] = useState(null);
    const [error, setError] = useState('');
    const location = useLocation();

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        if (eventId) {
            axios.get(`/api/events/${eventId}`, { headers: { authorization: localStorage.getItem('token') } })
                .then(response => {
                    setEvent(response.data);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    console.error(error);
                });
        } else {
            setError('Event ID not found');
        }
    }, [location]);

    if (error) {
        return <div>{error}</div>;
    }

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div className="fullpage">
            <Navbar />

            <div className=" container px-lg-5">
                <div className="futureEvents p-4 p-lg-5 bg-white rounded-3 text-center" >
                    <div className="m-4 m-lg-4">
                        <h1 className="display-5 fw-bold text-primary">{event.name}</h1>
                    </div>
                    <div className='row'>

                    <div className="event-details col-6">
                        <div>
                            <img className="img-fluid bsb-scale center bsb-hover-scale-up" loading="lazy" src="https://picsum.photos/1500/1000" alt="Living" />
                        </div>
                    </div>
                    <div className="event-details col-6 text-start text-info">
                        <p><strong>Descrizione:</strong> {event.description}</p>
                        <p><strong>Categoria:</strong> {event.category}</p>
                        <p><strong>Data:</strong> {new Date(event.date).toLocaleDateString()}</p>
                        <p><strong>Luogo:</strong> {event.location}</p>
                        {/* Puoi aggiungere ulteriori dettagli dell'evento qui */}
                    </div>
                    </div>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default EventDetails;
