import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'
import './App.css'

function Create() {

  const [user, setUser] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/homepage');
    } else {
      axios.get('/api/auth/verifyOrganizer', { headers: { authorization: token } })
        .then(response => {
          if (response.status !== 200) {
            navigate('/homepage');
          } else {
            setUser(response.data.user);
          }
        })
        .catch((error) => {
          console.error('Verification error:', error);
          navigate('/homepage');
        });
    }
  }, [navigate]);

  const [formData, setFormData] = useState({ name: '', description: '', date: null, category: 0 });
  const [errorMessage, setErrorMessage] = useState('');

  //Cambia il contenuto di formData in tempo reale mentre si scrive
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/organizzatore', formData);
      localStorage.setItem('token', response.data.token); // Salva il token
      navigate('/homepage');
    } catch (error) {
      console.error('Error:', error);
      setErrorMessage('Invalid login credentials.');
    }
