import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './App.css';

// Assicurati di avere installato e configurato il pacchetto 'react-google-maps'
import { GoogleMap, LoadScript, Autocomplete } from '@react-google-maps/api';

function CreateEvent() {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        date: '',
        place: '',
        category: 'cultura', // Valore di default
        images: []
    });
    const [user, setUser] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Recupera l'utente loggato e verifica il token
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                    }
                })
                .catch(error => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleImageChange = (e) => {
        setFormData(prevData => ({
            ...prevData,
            images: Array.from(e.target.files),
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const event = {
                ...formData,
                organizer: user._id, // Aggiungi l'ID dell'organizzatore
            };
            const response = await axios.post('/api/events', event);
            navigate('/homepage');
        } catch (error) {
            console.error('Error:', error);
            setErrorMessage('Errore nella creazione dell\'evento.');
        }
    };

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div>

            <Navbar />
            <div className="row justify-content-center">

            <div className="col-12 col-sm-10 col-md-8 col-lg-6 col-xl-5 col-xxl-4">
                <div className="card border border-light-subtle rounded-3 shadow-sm">
                    <div className="card-body p-3 p-md-4 p-xl-5">
                        <h2 className="fs-6 fw-normal text-center text-secondary mb-4">
                            Crea un nuovo evento
                        </h2>
                        <form onSubmit={handleSubmit}>
                            <div className="row gy-2 overflow-hidden">
                                <div className="col-12">
                                    <div className="form-floating mb-3">
                                        <input
                                            type="text"
                                            className="form-control border-primary"
                                            name="name"
                                            id="name"
                                            placeholder="Nome dell'evento"
                                            value={formData.name}
                                            onChange={handleChange}
                                            required
                                        />
                                        <label htmlFor="name" className="form-label text-primary">
                                            Nome dell'evento
                                        </label>
                                    </div>
                                </div>

                                <div className="col-12">
                                    <div className="form-floating mb-3">
                                        <textarea
                                            className="form-control border-primary"
                                            name="description"
                                            id="description"
                                            placeholder="Descrizione dell'evento"
                                            value={formData.description}
                                            onChange={handleChange}
                                            required
                                        />
                                        <label htmlFor="description" className="form-label text-primary">
                                            Descrizione
                                        </label>
                                    </div>
                                </div>

                                <div className="col-12">
                                    <div className="form-floating mb-3">
                                        <input
                                            type="datetime-local"
                                            className="form-control border-primary"
                                            name="date"
                                            id="date"
                                            placeholder="Data dell'evento"
                                            value={formData.date}
                                            onChange={handleChange}
                                            required
                                        />
                                        <label htmlFor="date" className="form-label text-primary">
                                            Data e ora
                                        </label>
                                    </div>
                                </div>

                                <div className="col-12">
                                    <div className="mb-3">
                                        <label htmlFor="place" className="form-label text-primary">
                                            Luogo
                                        </label>
                                        <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
                                            <Autocomplete
                                                onPlaceSelected={(place) => {
                                                    setFormData(prevData => ({
                                                        ...prevData,
                                                        place: place.formatted_address,
                                                    }));
                                                }}
                                                fields={['formatted_address']}
                                                className="form-control border-primary"
                                                placeholder="Seleziona il luogo"
                                                id="place"
                                            />
                                        </LoadScript>
                                    </div>
                                </div>

                                <div className="col-12">
                                    <div className="form-floating mb-3">
                                        <select
                                            className="form-select border-primary"
                                            name="category"
                                            id="category"
                                            value={formData.category}
                                            onChange={handleChange}
                                            required
                                        >
                                            <option value="cultura">Cultura</option>
                                            <option value="concerti">Concerti</option>
                                            <option value="mostre">Mostre</option>
                                            <option value="party">Party</option>
                                            <option value="sport">Sport</option>
                                        </select>
                                        <label htmlFor="category" className="form-label text-primary">
                                            Categoria
                                        </label>
                                    </div>
                                </div>

                                <div className="col-12">
                                    <div className="mb-3">
                                        <label htmlFor="images" className="form-label text-primary">
                                            Immagini
                                        </label>
                                        <input
                                            type="file"
                                            className="form-control border-primary"
                                            name="images"
                                            id="images"
                                            onChange={handleImageChange}
                                            multiple
                                        />
                                    </div>
                                </div>

                                {errorMessage && (
                                    <div className="col-12">
                                        <p className="text-danger text-center">{errorMessage}</p>
                                    </div>
                                )}

                                <div className="col-12">
                                    <div className="d-grid my-3">
                                        <button className="btn btn-primary btn-lg" type="submit">
                                            Crea Evento
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
        </div>
    );
}

export default CreateEvent;
