import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const Profile = () => {

    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user);
                        fetchPastEvents(response.data.user._id);
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    const fetchPastEvents = async (userId) => {
        await axios.get('/api/events/pastEvents', { headers: { authorization: sessionStorage.getItem('token') }, params: { userId } })
            .then(response => {
                setEvents(response.data || []);
            })
            .catch(error => {
                console.error('Error fetching events:', error);
            });
    }

    const handleEventClick = (eventId) => {
        navigate(`/evento?id=${eventId}`);
    }

    const handleDeleteUser = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.delete(`/api/auth/delete`, {
                headers: { authorization: sessionStorage.getItem('token') },
                params: { userId: user._id }
            });

            if (response.status === 200) {
                alert("Account eliminato con successo.");
                sessionStorage.removeItem('token');
                navigate('/register');
            } else {
                console.error('Errore durante l\'eliminazione:', response);
                alert('Impossibile eliminare l\'account.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Errore nella richiesta di eliminazione.');
        }
    };

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div className="d-flex flex-column min-vh-100 bg-dark text-white">
    <Navbar />

    <header className="py-5 flex-grow-1">
        <div className="container text-center">
            <h1 className="display-4 text-warning text-capitalize">{user.username}</h1>
        </div>

        <div className="container mt-5 d-flex row justify-content-center align-items-center">
            <div className="col-lg-6 col-12">
                <div className="futureEvents p-4 p-lg-5 bg-light rounded-3 text-center shadow-lg mb-5">
                    <h1 className="text-primary fw-bolder mb-3">Ha partecipato:</h1>
                    <div className="row gx-lg-5">
                        {pastEvents.length > 0 ? (
                            pastEvents.map((event) => {
                                const data = new Date(event.date);
                                return (
                                    <div key={event._id} className="event card col-12 col-xxl-6 col-lg-6 mb-5 border-0 bg-transparent text-start" onClick={() => handleEventClick(event._id)}>
                                        <figure className="card-img-top mb-0 overflow-hidden">
                                            <a className="overflow-hidden">
                                                {event.images.length > 0 ? (
                                                    <img src={`${event.images[0]}`} alt={`event`} className="img-thumbnail" />
                                                ) : (
                                                    <img src='/assets/img/logoNuovo.png' alt={`event`} className="img-thumbnail" />
                                                )}
                                            </a>
                                        </figure>

                                        <div className="card-body p-2 bg-light">
                                            <div className="entry-header row">
                                                <ul className="entry-meta list-unstyled d-flex mb-3">
                                                    <li className='col-6'>
                                                        <a className="text-capitalize d-inline-flex m-3 px-2 py-1 bg-secondary link-light text-accent-emphasis bg-accent-subtle border border-accent-subtle rounded-2 text-decoration-none fs-7">{event.category}</a>
                                                    </li>

                                                    <li className='col-6'>
                                                        <a className="fs-5 link-secondary m-3 text-decoration-none d-flex justify-content-end">
                                                            <span className="ms-2 fs-5">
                                                                {data.toLocaleDateString('it-IT', {
                                                                    year: 'numeric',
                                                                    month: 'numeric',
                                                                    day: 'numeric'
                                                                })}
                                                                ,
                                                                {data.toLocaleTimeString('it-IT', {
                                                                    hour: '2-digit',
                                                                    minute: '2-digit'
                                                                })}
                                                            </span>
                                                        </a>
                                                    </li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div className="card-footer border-0 bg-light p-0 m-0 mb-3">
                                            <h2 className="card-title entry-title h4 m-1 mb-3 text-center">
                                                <a className="link-primary text-decoration-none">{event.name}</a>
                                            </h2>
                                        </div>
                                    </div>
                                );
                            })
                        ) : (
                            <div className="mt-3 text-muted">Nessun evento passato</div>
                        )}
                    </div>
                </div>
            </div>

            <div className="col-lg-6 col-12">
                <div className="futureEvents p-4 p-lg-5 bg-light rounded-3 text-center shadow-lg mb-5">
                    <h1 className="text-primary fw-bolder mb-3">Parteciperà a:</h1>
                    <div className="row gx-lg-5">
                        {futureEvents.length > 0 ? (
                            futureEvents.map((event) => {
                                const data = new Date(event.date);
                                return (
                                    <div key={event._id} className="event card col-12 col-xxl-6 col-lg-6 mb-5 border-0 bg-transparent text-start" onClick={() => handleEventClick(event._id)}>
                                        <figure className="card-img-top mb-0 overflow-hidden">
                                            <a className="overflow-hidden">
                                                {event.images.length > 0 ? (
                                                    <img src={`${event.images[0]}`} alt={`event`} className="img-thumbnail" />
                                                ) : (
                                                    <img src='/assets/img/logoNuovo.png' alt={`event`} className="img-thumbnail" />
                                                )}
                                            </a>
                                        </figure>

                                        <div className="card-body p-2 bg-light">
                                            <div className="entry-header row">
                                                <ul className="entry-meta list-unstyled d-flex mb-3">
                                                    <li className='col-6'>
                                                        <a className="text-capitalize d-inline-flex m-3 px-2 py-1 bg-secondary link-light text-accent-emphasis bg-accent-subtle border border-accent-subtle rounded-2 text-decoration-none fs-7">{event.category}</a>
                                                    </li>

                                                    <li className='col-6'>
                                                        <a className="fs-5 link-secondary m-3 text-decoration-none d-flex justify-content-end">
                                                            <span className="ms-2 fs-5">
                                                                {data.toLocaleDateString('it-IT', {
                                                                    year: 'numeric',
                                                                    month: 'numeric',
                                                                    day: 'numeric'
                                                                })}
                                                                ,
                                                                {data.toLocaleTimeString('it-IT', {
                                                                    hour: '2-digit',
                                                                    minute: '2-digit'
                                                                })}
                                                            </span>
                                                        </a>
                                                    </li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div className="card-footer border-0 bg-light p-0 m-0 mb-3">
                                            <h2 className="card-title entry-title h4 m-1 mb-3 text-center">
                                                <a className="link-primary text-decoration-none">{event.name}</a>
                                            </h2>
                                        </div>
                                    </div>
                                );
                            })
                        ) : (
                            <div className="mt-3 text-muted">Nessun evento futuro</div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    </header>

    <Footer />
</div>

    );
};

export default Profile;
