import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, AdvancedMarkerElement } from '@react-google-maps/api';
import Navbar from './Navbar';
import Footer from './Footer';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

function Map() {
    const [map, setMap] = useState(null);

    useEffect(() => {
        if (map) {
            // Crea e aggiungi AdvancedMarkerElement
            new google.maps.marker.AdvancedMarkerElement({
                position: center,
                map: map,
                content: '<img src="https://cdn-icons-png.freepik.com/512/2886/2886372.png" width="50" height="50"/>'
            });
        }
    }, [map]);

    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow'>
                    <LoadScript googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs">
                        <GoogleMap
                            mapContainerStyle={mapContainerStyle}
                            center={center}
                            zoom={15}
                            onLoad={mapInstance => setMap(mapInstance)}  // Imposta l'istanza della mappa
                        />
                    </LoadScript>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Map;
