import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { GoogleMap, LoadScript } from '@react-google-maps/api';
import Navbar from './Navbar';
import Footer from './Footer';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

// Sostituisci con il tuo ID mappa
const mapId = '6e674a5f15d5b512'; 

function Map() {
    const [markers, setMarkers] = useState([]);
    const mapRef = useRef(null);

    useEffect(() => {
        // Carica i dati dei marker dal tuo DB
        axios.get('/api/events/markers')  // Sostituisci con l'endpoint corretto
            .then(response => {
                setMarkers(response.data);
                console.log(response.data);  // Assicurati che i dati siano nella forma giusta
            })
            .catch(error => {
                console.error('Error fetching marker data:', error);
            });
    }, []);

    const onMapLoad = (mapInstance) => {
        mapRef.current = mapInstance;

        // Verifica che google sia definito
        if (window.google && window.google.maps) {
            markers.forEach(marker => {
                new window.google.maps.marker.AdvancedMarkerElement({
                    map: mapRef.current,
                    position: { lat: marker.lat, lng: marker.lng },
                    content: `<div style="color: red;">${marker.name}</div>`, // Personalizza il contenuto
                    // Puoi anche aggiungere altre opzioni qui come `icon` o `title`
                });
            });
        } else {
            console.error('Google Maps API is not loaded.');
        }
    };

    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow map'>
                    <LoadScript googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs">
                        <GoogleMap
                            mapContainerStyle={mapContainerStyle}
                            center={center}
                            zoom={15}
                            onLoad={onMapLoad}
                            options={{ mapId }}  // Passa l'ID mappa qui
                        />
                    </LoadScript>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Map;
