import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

import './App.css'

function Map() {

    const [user, setUser] = useState(null);

    const navigate = useNavigate();
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/mappa');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/mappa');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                        initializeMap(); // Inizializza la mappa solo dopo che l'utente è stato impostato
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/mappa');
                });
        }
    }, [navigate]);

    const initializeMap = () => {
        // Assicurati che l'elemento DOM per la mappa esista
        const mapElement = document.getElementById('map');
        if (mapElement) {
            const map = L.map(mapElement).setView([37.51136, 15.08709], 15);

            // Aggiunge il layer di OpenStreetMap
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                attribution: '&copy; OpenStreetMap contributors'
            }).addTo(map);

            // Aggiunge un marker alla mappa
            // L.marker([39.51136, 18.14564]).addTo(map)
            //     .bindPopup('Popup <br> Easily customizable.')
            //     .openPopup();
        } else {
            console.error('Map container not found');
        }
    };




    if(!user){
        return <div>Caricamento in corso...</div>
    }

    return (
        // <div classnName="Dashboard container">
