        //         <div className="btn-group btn-group-lg" role="group" aria-label="Basic example">
        //             <img className="col-1 btn btn-primary" src="/assets/img/logo.png"></img>
        //             <a href="#" className="col-7 btn btn-primary">Home</a>
        //             <select name="Category" value="category" className="col-7 btn btn-primary">Categoria</select>
        //             <a hreg="#" className="col-7 btn btn-primary">Chat</a>
        //             <a hreg="#" className="col-7 btn btn-primary">Profilo</a>
        //         </div>
        //     </div>
        // </div>

        <div className="Map justify-content-center">

            <Navbar />

            {/* ------------------------------------ SPAZIO SU --------------------------------------------- */}

        <div>
            <div className=' m-5 ' id="map" style={{ height: '600px' }}></div>;
        </div>


        </div>


    );
}

export default Map;
