import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

import './App.css'

function Map() {

    const [user, setUser] = useState(null);

    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    useEffect(() => {
        // Crea la mappa e imposta la vista iniziale
        const map = L.map('map').setView([51.505, -0.09], 13);

        // Aggiunge il layer di OpenStreetMap
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '&copy; OpenStreetMap contributors'
        }).addTo(map);

        // Aggiunge un marker alla mappa
        L.marker([51.5, -0.09]).addTo(map)
          .bindPopup('A pretty CSS3 popup.<br> Easily customizable.')
          .openPopup();
      }, []);



    if(!user){
        return <div>Caricamento in corso...</div>
    }

    return (
        // <div classnName="Dashboard container">
        //     <div className="row justify-content-center">

        //         <div className="btn-group btn-group-lg" role="group" aria-label="Basic example">
        //             <img className="col-1 btn btn-primary" src="/assets/img/logo.png"></img>
        //             <a href="#" className="col-7 btn btn-primary">Home</a>
        //             <select name="Category" value="category" className="col-7 btn btn-primary">Categoria</select>
        //             <a hreg="#" className="col-7 btn btn-primary">Chat</a>
        //             <a hreg="#" className="col-7 btn btn-primary">Profilo</a>
        //         </div>
        //     </div>
        // </div>

        <div className="Map ">

            <Navbar />

            {/* ------------------------------------ SPAZIO SU --------------------------------------------- */}

            <div className="m-5 p-2">
                <div></div>
            </div>

            {/* ------------------------------------ MAPPA --------------------------------------------- */}

            <section className="pt-4">
                <div className="container px-lg-5">
                    <div className="row p-5 p-lg-5 gx-lg-5">
                        <iframe src="https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d12660.047486303667!2d15.078201365254122!3d37.50763815313888!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sit!2sit!4v1722606557203!5m2!1sit!2sit" width="800" height="600" allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
                    </div>
                </div>
            </section>



        </div>


    );
}

export default Map;
