import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import io from 'socket.io-client';
import Navbar from './Navbar';
import Footer from './Footer';

const EventDetails = () => {
    const token = sessionStorage.getItem('token');
    const [event, setEvent] = useState(null);
    const [user, setUser] = useState(null);
    const [comments, setComments] = useState([]);
    const [error, setError] = useState('');
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const socket = io('http://localhost:3000'); // Collegarsi al server Socket.IO

        // Ascolta l'evento `newComment`
        socket.on('newComment', (newComment) => {
            setComments((prevComments) => [...prevComments, newComment]);
        });

        // Cleanup della connessione Socket.IO
        return () => socket.disconnect();
    }, []);

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        if (eventId) {
            axios.get(`/api/events/${eventId}`, { headers: { authorization: sessionStorage.getItem('token') } })
                .then(response => {
                    setEvent(response.data);
                    fetchEventComments(eventId);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    console.error(error);
                });

            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/homepage');
                    } else {
                        setUser(response.data.user);
                    }
                })
                .catch(error => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
        } else {
            setError('Event ID not found');
        }
    }, [location]);

    const fetchEventComments = async (eventId) => {
        await axios.get(`/api/events/commentsOf`, { headers: { eventId } })
            .then(response => {
                setComments(response.data);
            })
            .catch(error => {
                setError('Failed to load comments');
                console.error(error);
            });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const queryParams = new URLSearchParams(location.search);

        try {
            const comment = {
                text: formData.text,
                user: user._id, 
                event: queryParams.get('id'), 
                date: new Date()
            };

            const response = await axios.post('/api/events/addComment', comment);

            if (response.status === 201) {
                setFormData({ text: '' });
            } else {
                console.error('Errore durante l\'aggiunta del commento:', response);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    if (error) {
        return <div>{error}</div>;
    }

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div className="fullpage">
            <Navbar />
            <div className="container px-lg-5">
                <div className="futureEvents p-4 p-lg-5 bg-white rounded-3 text-center">
                    {/* Dettagli evento */}
                    <section>
                        <h4 className="m-5 mb-0 text-danger fw-bold">Commenti</h4>
                        <div className="container my-5 mt-0 py-5">
                            <div className="row d-flex justify-content-center">
                                <div className="col-md-12 col-lg-10">
                                    <div className="card text-body shadow">
                                        <div className="card-body p-4">
                                            {comments.length > 0 ? (
                                                comments.map((comment, index) => (
                                                    <div key={index} className="d-flex flex-start mb-4">
                                                        <div>
                                                            <div className="d-flex mb-0 row">
                                                                <h6 className="text-danger fw-bold mb-3 col-6 text-end">
                                                                    {comment.user.username || 'Anonimo'}
                                                                </h6>
                                                                <span className="mb-3 col-6 text-end text-warning">
                                                                    {new Date(comment.date).toLocaleDateString()}
                                                                </span>
                                                            </div>
                                                            <p className="mb-0">
                                                                {comment.text}
                                                            </p>
                                                        </div>
                                                    </div>
                                                ))
                                            ) : (
                                                <div>
                                                    <h6 className='text-black'>Ancora nessun commento</h6>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default EventDetails;
