import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'
import './App.css'

function Organizer() {

    const [user, setUser] = useState(null);

    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/homepage');
        } else {
            axios.get('/api/auth/verifyOrganizer', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/homepage');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
        }
    }, [navigate]);

    const [formData, setFormData] = useState({ name: '', description : '', date: null, category: 0 });
    const [errorMessage, setErrorMessage] = useState('');

    //Cambia il contenuto di formData in tempo reale mentre si scrive
    const handleChange = (e) => {
      const { name, value, type, checked } = e.target;
      setFormData((prevData) => ({
        ...prevData,
        [name]: type === 'checkbox' ? checked : value,
      }));
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
          const response = await axios.post('/', formData);
          localStorage.setItem('token', response.data.token); // Salva il token
          navigate('/homepage');
      } catch (error) {
          console.error('Error:', error);
          setErrorMessage('Invalid login credentials.');
      } 
    };

    if(!user){
        return <div>Caricamento in corso...</div>
    }

    return (

        <div className="Organizer ">

            <Navbar />

            {/* ------------------------------------ SPAZIO SU --------------------------------------------- */}


            {/* ------------------------------------ FORM CREA EVENTO --------------------------------------------- */}

            <form onSubmit={handleSubmit}>
                  <div className="row gy-2 overflow-hidden">
                    <div className="col-12">
                      <div className="form-floating mb-3">
                        <input
                          type="email"
                          className="form-control border-primary"
                          name="emailIn"
                          id="emailIn"
                          placeholder="name@example.com"
                          value={formData.emailIn}
                          onChange={handleChange}
                          required
                        />
                        <label htmlFor="emailIn" className="form-label text-primary">
                          Email
                        </label>
                      </div>
                    </div>
                    <div className="col-12">
                      <div className="form-floating mb-3">
                        <input
                          type="password"
                          className="form-control border-primary"
                          name="passwordIn"
                          id="passwordIn"
                          placeholder="password"
                          value={formData.passwordIn}
                          onChange={handleChange}
                          required
                        />
                        <label htmlFor="passwordIn" className="form-label text-primary">
                          Passowrd
                        </label>
                      </div>
                    </div>
                    {errorMessage && (
                      <div className="col-12">
                        <p className="text-danger text-center">{errorMessage}</p>
                      </div>
                    )}

                    <div className="col-12">
                      <div className="d-grid my-3">
                        <button className="btn btn-primary btn-lg" type="submit">
                          Entra
                        </button>
                      </div>
                    </div>
                    <div className="col-12">
                      <p className="m-0 text-dark text-center">
                        Non hai ancora un account? <a href="./register" className="link-danger text-decoration-none text-dark">Registrati ora!</a>
                      </p>
                    </div>
                  </div>
                </form>    
        </div>


    );
}

export default Organizer;
