import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, NavLink } from 'react-router-dom';
import './App.css'

function Navbar() {

    const [user, setUser] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    const logout = () => {
        sessionStorage.removeItem('token');
        navigate('/login');
    }

    const handleSearch = (event) => {
        event.preventDefault();
        axios.get('/api/events/search', { params: { search: searchTerm } })
            .then(response => {
                setSearchResults(response.data);
            })
            .catch(error => {
                console.error('Search error:', error);
            });
    };

    const handleInputChange = (event) => {
        setSearchTerm(event.target.value);
    };

    if (!user) {
        return <div>Caricamento in corso...</div>
    }

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-success fixed-top shadow">
                <div className="container-fluid">
                    <a className="navbar-brand" href="/homepage">
                        <img src="/assets/img/logoNuovo.png" alt="" className="d-inline-block align-text-top" style={{ width: '50px', height: '50px' }} />
                    </a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <NavLink to="/homepage" activeClassName="active" className="nav-link text-white">
                                    Home
                                </NavLink>
                            </li>
                            <li className="nav-item dropdown">
                                <NavLink to="/categorie" activeClassName="active" className="nav-link dropdown-toggle text-white" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                    Categorie
                                </NavLink>
                                <ul className="dropdown-menu">
                                    <li><NavLink to="/categorie?c=tutto" className="dropdown-item">Tutte</NavLink></li>
                                    <li><NavLink to="/categorie?c=cultura" className="dropdown-item">Cultura</NavLink></li>
                                    <li><NavLink to="/categorie?c=concerti" className="dropdown-item">Concerti</NavLink></li>
                                    <li><NavLink to="/categorie?c=mostre" className="dropdown-item">Mostre</NavLink></li>
                                    <li><NavLink to="/categorie?c=party" className="dropdown-item">Party</NavLink></li>
                                    <li><NavLink to="/categorie?c=sport" className="dropdown-item">Sport</NavLink></li>
                                </ul>
                            </li>
                            <li className="nav-item">
                                <NavLink to="/mappa" activeClassName="active" className="nav-link text-white">
                                    Mappa
                                </NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink to="/chat" activeClassName="active" className="nav-link text-white">
                                    Chat
                                </NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink to="/profilo" activeClassName="active" className="nav-link text-white">
                                    Profilo
                                </NavLink>
                            </li>
                            <li className="nav-item">
                                <NavLink to="/organizzatore" activeClassName="active" className={`nav-link ${user.organizer ? 'text-white ' : 'disabled'}`} href={`${user.organizer ? '/organizzatore' : ''}`} aria-disabled={!user.organizer}>
                                    Organizzatore
                                </NavLink>
                            </li>
                        </ul>
                        <form className="d-flex" role="search" onSubmit={handleSearch}>
                            <input
                                className="form-control me-2"
                                type="search"
                                placeholder="Search"
                                aria-label="Search"
                                value={searchTerm}
                                onChange={handleInputChange}
                            />

                            {searchResults.length > 0 ? (      
                            <div class="dropdown">
                                <button class="btn btn-outline-light dropdown-toggle" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-expanded="false">
                                    Search
                                </button>
                                <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton">

                                            searchResults.map(event => (
                                                <div key={event._id} className='m-2'>
                                                    <li><a class="dropdown-item" href={`/event?id=${event._id}`}>{event.name}</a></li>
                                                </div>
                                            ))

                                </ul>
                            </div>
                            ): (
                                <button class="btn btn-outline-light" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-expanded="false">
                                    Search
                                </button>
                            )}

                        </form>

                        <ul className='navbar-nav mb-2 mb-lg-0 mt-lg-0 mt-3 align-items-center'>
                            <li className='nav-item'>
                                <a onClick={() => logout()} className='btn btn-danger text-white ms-lg-5'>
                                    LogOut
                                </a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>


            <div className="m-5 p-2"></div>
            <div className="m-0 p-2">
                {searchResults.length > 0 && (
                    <div className='bg-white border-radius'>
                        <h4>Risultati della ricerca:</h4>

                        {searchResults.map(event => (
                            <div key={event._id} className='m-2'>
                                <a href={`/event/${event._id}`} className='link-primary m-5'><h5>{event.name}</h5></a>
                            </div>
                        ))}
                    </div>
                )}
            </div>


        </div>

    );
}

export default Navbar;
