import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const Category = () => {
    const location = useLocation();
    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);
    const [categoria, setCategoria] = useState('');
    const navigate = useNavigate();

    const fetchEventi = async () => {
        const query = new URLSearchParams(location.search);
        const categoriaParam = query.get('c');
        setCategoria(categoriaParam || 'tutto');
        try {
            const response = await axios.get(`/api/events/category`, {
                headers: { authorization: sessionStorage.getItem('token') },
                params: { category: categoriaParam },
            });
            setEvents(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error('Error fetching events:', error);
        }
    };

    useEffect(() => {
        const verifyUser = async () => {
            try {
                const response = await axios.get('/api/auth/verify', {
                    headers: { authorization: sessionStorage.getItem('token') },
                });
                if (response.status === 200) {
                    setUser(response.data.user);
                    fetchEventi();
                } else {
                    navigate('/homepage');
                }
            } catch (error) {
                console.error('Verification error:', error);
                navigate('/homepage');
            }
        };
        verifyUser();
    }, [location.search, navigate]);

    const handleEventClick = (eventId) => {
        navigate(`/evento?id=${eventId}`);
    };

    return (
        <div className="d-flex flex-column min-vh-100 bg-dark">
            <Navbar />
            <header className="py-5 flex-grow-1">
                <div className="container px-lg-5">
                    <div className="p-4 p-lg-5 bg-white rounded-3 shadow text-center">
                        <div className="mb-4">
                            <h1 className="display-5 fw-bold text-primary text-capitalize">{categoria}</h1>
                        </div>
                        <div className="row gx-lg-5">
                            {events.length > 0 ? (
                                events.map((event) => {
                                    const data = new Date(event.date);
                                    return (
                                        <div key={event._id} className="col-lg-6 col-xxl-4 mb-5">
                                            <div className="card h-100 shadow-sm border-0" onClick={() => handleEventClick(event._id)}>
                                                <div className="overflow-hidden" style={{ cursor: 'pointer' }}>
                                                    {event.images.length > 0 ? (
                                                        <img src={`${event.images[0]}`} alt={`event`} className="card-img-top img-fluid rounded-top" />
                                                    ) : (
                                                        <img src='/assets/img/logoNuovo.png' alt={`event`} className="card-img-top img-fluid rounded-top" />
                                                    )}
                                                </div>
                                                <div className="card-body p-4 bg-light">
                                                    <div className="entry-header row mb-3">
                                                        <ul className="list-unstyled d-flex justify-content-between mb-0">
                                                            <li>
                                                                <span className="badge bg-secondary text-uppercase">{event.category}</span>
                                                            </li>
                                                            <li>
                                                                <span className="text-muted">
                                                                    {data.toLocaleDateString('it-IT', {
                                                                        year: 'numeric',
                                                                        month: 'numeric',
                                                                        day: 'numeric'
                                                                    })}
                                                                    , 
                                                                    {data.toLocaleTimeString('it-IT', {
                                                                        hour: '2-digit',
                                                                        minute: '2-digit'
                                                                    })}
                                                                </span>
                                                            </li>
                                                        </ul>
                                                    </div>
                                                    <h5 className="card-title text-center">
                                                        <a className="text-dark text-decoration-none">{event.name}</a>
                                                    </h5>
                                                </div>
                                                <div className="card-footer bg-light border-0 text-center py-3">
                                                    <a className="text-primary text-decoration-none fw-bold">
                                                        Scopri evento
                                                    </a>
                                                </div>
                                            </div>
                                        </div>
                                    );
                                })
                            ) : (
                                <div className="col-12 text-center">
                                    <p className="text-muted">Nessun evento in programma</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </header>
            <Footer />
        </div>
    );
};

export default Category;
