import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './App.css';
import Navbar from './Navbar';
import LocationSearchInput from './LocationSearchInput';

function CreateEvent() {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        date: null,
        location: '',
        category: 'cultura', // Valore di default
        images: [],
        latitude: '', // Aggiungi il campo per la latitudine
        longitude: '', // Aggiungi il campo per la longitudine
    });

    const [user, setUser] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verifyOrganizer', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/organizzatore');
                    } else {
                        setUser(response.data.user);// Imposta l'utente
                    }
                })
                .catch(error => {
                    console.error('Verification error:', error);
                    navigate('/organizzatore');
                });
        }
    }, [navigate]);

    useEffect(() => {
        if (user) {
            setFormData((prevData) => ({
                ...prevData,
                organizer: user._id, // Imposta l'organizzatore nel formData
            }));
        }
    }, [user]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        const imageUrls = files.map(file => {
            // Converti i file in URL temporanei o caricali su un server e ottieni gli URL
            return URL.createObjectURL(file);
        });

        setFormData(prevData => ({
            ...prevData,
            images: imageUrls // Assicurati che siano stringhe valide
        }));
    };

    const handleLocationSelect = (coordinates) => {
        setFormData(prevData => ({
            ...prevData,
            latitude: coordinates.latitude,
            longitude: coordinates.longitude // Opzionale: mostra il luogo come stringa
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const event = {
                ...formData,
                organizer: user._id, // Assicurati che l'ID dell'organizzatore venga incluso
            };
            console.log(event);
            const response = await axios.post('/api/event/create', event);
            navigate('/organizzatore');
        } catch (error) {
            console.error('Error:', error);
            setErrorMessage('Errore nella creazione dell\'evento.');
        }
    };

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div className="Create fullpage">
            <Navbar />
            <div className="row justify-content-center">
                <div className="col-12 col-sm-10 col-md-8 col-lg-6 col-xl-5 col-xxl-4">
                    <div className="card border border-light-subtle rounded-3 shadow-sm">
                        <div className="card-body p-3 p-md-4 p-xl-5">
                            <h2 className="fs-1 fw-bold text-center text-danger mb-4">
                                Crea un nuovo evento
                            </h2>
                            <form onSubmit={handleSubmit}>
                                <div className="row gy-2 overflow-hidden">
                                    <div className="col-12">
                                        <div className="form-floating mb-3">
                                            <input
                                                type="text"
                                                className="form-control border-danger"
                                                name="name"
                                                id="name"
                                                placeholder="Nome dell'evento"
                                                value={formData.name}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="name" className="form-label text-warning">
                                                Nome dell'evento
                                            </label>
                                        </div>
                                    </div>

                                    <div className="col-12">
                                        <div className="form-floating mb-3">
                                            <textarea
                                                className="form-control border-danger"
                                                name="description"
                                                id="description"
                                                placeholder="Descrizione dell'evento"
                                                value={formData.description}
                                                onChange={handleChange}

                                            />
                                            <label htmlFor="description" className="form-label text-warning">
                                                Descrizione
                                            </label>
                                        </div>
                                    </div>

                                    <div className="col-12">
                                        <div className="form-floating mb-3">
                                            <input
                                                type="datetime-local"
                                                className="form-control border-danger "
                                                name="date"
                                                id="date"
                                                placeholder="Data dell'evento"
                                                value={formData.date}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="date" className="form-label text-warning">
                                                Data e ora
                                            </label>
                                        </div>
                                    </div>

                                    <div className="col-12">
                                        <div className="mb-3">
                                            <label htmlFor="place" className="form-label text-warning">
                                                Luogo : &ensp;
                                            </label>
                                            <LocationSearchInput onSelect={handleLocationSelect} />
                                        </div>
                                    </div>

                                    <div className="col-12">
                                        <div className="form-floating mb-3">
                                            <select
                                                className="form-select border-danger"
                                                name="category"
                                                id="category"
                                                value={formData.category}
                                                onChange={handleChange}
                                                required
                                            >
                                                <option value="cultura">Cultura</option>
                                                <option value="concerti">Concerti</option>
                                                <option value="mostre">Mostre</option>
                                                <option value="party">Party</option>
                                                <option value="sport">Sport</option>
                                            </select>
                                            <label htmlFor="category" className="form-label text-warning">
                                                Categoria
                                            </label>
                                        </div>
                                    </div>

                                    <div className="col-12">
                                        <div className="mb-3">
                                            <label htmlFor="images" className="form-label text-warning">
                                                Immagini
                                            </label>
                                            <input
                                                type="file"
                                                className="form-control border-danger"
                                                name="images"
                                                id="images"
                                                onChange={handleImageChange}
                                                multiple
                                            />
                                        </div>
                                    </div>

                                    {errorMessage && (
                                        <div className="col-12">
                                            <p className="text-danger text-center">{errorMessage}</p>
                                        </div>
                                    )}

                                    <div className="col-12">
                                        <div className="d-grid my-3">
                                            <button className="btn btn-danger btn-lg" type="submit">
                                                Crea Evento
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CreateEvent;
