import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import Navbar from './Navbar';
import Footer from './Footer';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

function Map() {
    const [user, setUser] = useState(null);
    const [markerPosition, setMarkerPosition] = useState({
        lat: 37.51136,
        lng: 15.08709,
    });

    const navigate = useNavigate();
    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/mappa');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/mappa');
                    } else {
                        setUser(response.data.user);
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/mappa');
                });
        }
    }, [navigate]);

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow'>
                    <LoadScript googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs">
                        <GoogleMap
                            mapContainerStyle={mapContainerStyle}
                            center={markerPosition}
                            zoom={15}
                        >
                        <Marker
                            position={markerPosition}
                            icon={{
                                url: '/assets/img/logoNuovo.png',  // Il percorso della tua immagine
                                scaledSize: window.google ? new window.google.maps.Size(50, 50) : undefined, // Dimensione dell'icona
                                origin: window.google ? new window.google.maps.Point(0, 0) : undefined, // Origine dell'immagine
                                anchor: window.google ? new window.google.maps.Point(25, 50) : undefined, // Punto di ancoraggio (centro inferiore)
                              }} 
                        />
                        </GoogleMap>
                    </LoadScript>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Map;
