import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

const mapId = '6e674a5f15d5b512';

function Mappa() {
    const [markers, setMarkers] = useState([]);

    useEffect(() => {
        const fetchMarkers = async () => {
            try {
                const response = await axios.get('/api/events/markers');  // Sostituisci con l'endpoint corretto
                setMarkers(response.data);
                console.log('Fetched markers:', response.data);
            } catch (error) {
                console.error('Error fetching marker data:', error);
            }
        };

        fetchMarkers();
    }, []);

    return (
        <div className="Mappa justify-content-center fullpage">
            <Navbar />
            <LoadScript
                googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs"  // Sostituisci con la tua chiave API
                mapIds={[mapId]}
            >
                <div className='m-5 shadow map' id='map'>

                        <GoogleMap
                            mapContainerStyle={mapContainerStyle}
                            center={center}
                            zoom={15}
                        >
                            {markers.map((marker, index) => (
                                <Marker
                                    key={index}
                                    position={{ lat: marker.latitude, lng: marker.longitude }}
                                />
                            ))}
                        </GoogleMap>

                </div>
            </LoadScript>
            <Footer />
        </div>
    );
}

export default Mappa;
