import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';


const EventDetails = () => {
    const [event, setEvent] = useState(null);
    const [comments, setComments] = useState([]);
    const [error, setError] = useState('');
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        //Prendo parametro id dall'url 
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        if (eventId) {
            axios.get(`/api/events/${eventId}`, { headers: { authorization: sessionStorage.getItem('token') } })
                .then(response => {
                    setEvent(response.data);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    navigate('/homepage');
                    console.error(error);
                });
            axios.get(`/api/events/commentsOf`, { headers: {eventId} })
                .then(response => {
                    setComments(response.data);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    navigate('/homepage');
                    console.error(error);
                });
        } else {
            setError('Event ID not found');
        }
    }, [location]);

    if (error) {
        return <div>{error}</div>;
    }

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div className="fullpage">
            <Navbar />

            <div className=" container px-lg-5">
                <div className="futureEvents p-4 p-lg-5 bg-white rounded-3 text-center" >
                    <div className="m-4 m-lg-4">
                        <h1 className="display-5 fw-bold text-primary">{event.name}</h1>
                    </div>
                    <div className='row'>

                    <div className="event-details col-6">
                        <div>
                            <img className="img-fluid bsb-scale center bsb-hover-scale-up" loading="lazy" src="https://picsum.photos/1500/1000" alt="Living" />
                        </div>
                    </div>
                    <div className='col-6'>
                    <div className="event-details text-start text-secondary">
                        {(event.description === '' ) ?  (<div></div>) : (<h5>{event.description}</h5>) }
                        <h5 className='text-capitalize'><strong>Categoria:</strong> {event.category}</h5>
                        <h5><strong>Data:</strong> {new Date(event.date).toLocaleDateString()}</h5>
                        <h5><strong>Ora:</strong> {new Date(event.date).toLocaleTimeString()}</h5>
                        <h5><strong>Luogo:</strong> {event.location}</h5>
                        {/* Puoi aggiungere ulteriori dettagli dell'evento qui */}

                    </div>
                </div>
                </div>

                    <h4 className=' col-6 m-5 text-danger'>Commenti</h4>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default EventDetails;
