import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { useNavigate, useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';


const serverUrl = window.location.hostname === 'localhost'
    ? 'http://localhost:5000'
    : 'http://192.168.1.25:5000';

const socket = io('http://192.168.1.25:5000');


const EventDetails = () => {
    const token = sessionStorage.getItem('token');
    const [event, setEvent] = useState(null);
    const [user, setUser] = useState(null);
    const [comments, setComments] = useState([]);
    const [error, setError] = useState('');
    const [formData, setFormData] = useState({
        text: '',
    });
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        if (eventId) {
            axios.get(`/api/events/${eventId}`, { headers: { authorization: token } })
                .then(response => {
                    setEvent(response.data);
                    fetchEventComments(eventId);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    console.error(error);
                });

            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/homepage');
                    } else {
                        setUser(response.data.user);
                    }
                })
                .catch(error => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
        } else {
            setError('Event ID not found');
        }

        socket.on('newComment', (savedComment) => {
            console.log("Received new comment: ", savedComment);
            setComments(prevComments => {
                const updatedComments = [...prevComments, savedComment];
                updatedComments.sort((a, b) => new Date(b.date) - new Date(a.date));
                return updatedComments;
            });

        });

        return () => {
            socket.off('newComment');
        };
    }, [location, token, navigate]);

    const fetchEventComments = async (eventId) => {
        try {
            const response = await axios.get('/api/events/commentsOf', { headers: { event: eventId } });
            const comments = response.data.comments || [];
            comments.sort((a, b) => new Date(b.date) - new Date(a.date));
            setComments(comments);
        } catch (error) {
            setError('Failed to load comments');
            console.error(error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value,
        }));
    };


    const handlePartecipate = (e) => {
        e.preventDefault();

    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const queryParams = new URLSearchParams(location.search);
        const comment = {
            text: formData.text,
            user: user._id,
            event: queryParams.get('id'),
            data: new Date()
        };

        try {
            const response = await axios.post('/api/events/addComment', comment);
            console.log(response);
            if (response.status === 201) {

                socket.emit('sendComment', response.data);
                setFormData({ text: '' });
            } else {
                console.error('Error adding comment:', response);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    if (error) {
        return <div>{error}</div>;
    }

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div className="fullpage">
            <Navbar />

            <div className="container px-lg-5">
                <div className="futureEvents p-4 p-lg-5 bg-white rounded-3 text-center">
                    <div className="m-4 m-lg-4 row">
                        <h1 className="display-5 fw-bold text-primary col-lg-11">{event.name}</h1>
                        <a className="btn btn-primary btn-lg center col-lg-1" name="partecipa" id="partecipa" href='/organizzatore/crea'>

                        </a>
                        <label htmlFor="partecipa" className="form-label text-secondary">
                                            Commento...
                                        </label>
                    </div>
                    <div className='row'>
                        <div className="event-details col-lg-6 col-sm-12">
                            <div className='eventDetail'>
                                <img className="img-fluid bsb-scale center bsb-hover-scale-up shadow" loading="lazy" src="https://picsum.photos/1500/1000" alt="Living" />
                            </div>
                        </div>
                        <div className='col-lg-6 col-sm-12 mt-3'>
                            <div className="event-details text-start text-secondary">
                                {event.description ? <h5>{event.description}</h5> : null}
                                <h5 className='text-capitalize'><strong>Categoria:</strong> {event.category}</h5>
                                <h5><strong>Data:</strong> {new Date(event.date).toLocaleDateString()}</h5>
                                <h5><strong>Ora:</strong> {new Date(event.date).toLocaleTimeString()}</h5>
                                <h5><strong>Luogo:</strong> {event.location}</h5>
                            </div>
                            <form onSubmit={handleSubmit} className=''>
                                <div className="col-12 mt-3">
                                    <div className="form-floating mb-3">
                                        <textarea
                                            className="form-control border-primary inserisciCommento"
                                            name="text"
                                            id="text"
                                            placeholder="Commento..."
                                            rows="3"
                                            value={formData.text}
                                            onChange={handleChange}
                                            required
                                        ></textarea>
                                        <label htmlFor="text" className="form-label text-secondary">
                                            Commento...
                                        </label>
                                    </div>
                                    <div className="d-flex justify-content-end">
                                        <button type="submit" className="btn btn-danger">
                                            Send
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                    <section>
                        <h4 className="m-5 mb-0 text-danger fw-bold">Commenti</h4>
                        <div className="container my-5 mt-0 py-5">
                            {comments.length > 0 ? (
                                comments.map((comment, index) => (
                                    <div key={index} className="row d-flex justify-content-center">
                                        <div className="col-md-12 col-lg-10">
                                            <div className="comment card text-body shadow">
                                                <div className="card-body p-4 comment">
                                                    <div>
                                                        <div className="d-flex mb-3 row">
                                                            <h6 className="text-danger fw-bold ps-5 col-6 text-start">{comment.user.username || 'Anonimo'}</h6>
                                                            <span className="col-6 pe-5 text-end text-warning p-0">
                                                                {new Date(comment.date).toLocaleDateString()}
                                                            </span>
                                                        </div>
                                                        <p className="mb-0 shadow p-5 boder-light border bg-white">
                                                            {comment.text}
                                                        </p>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))
                            ) : (
                                <div>
                                    <h6 className='fw-light text-black'>Ancora nessun commento</h6>
                                </div>
                            )}
                        </div>
                    </section>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default EventDetails;
