import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const Profile = () => {

    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user);
                        fetchPastEvents(response.data.user._id);
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    const fetchPastEvents = async (userId) => {
        await axios.get('/api/events/pastEvents', { headers: { authorization: sessionStorage.getItem('token') }, params: { userId } })
            .then(response => {
                setEvents(response.data || []);
            })
            .catch(error => {
                console.error('Error fetching events:', error);
            });
    }

    const handleEventClick = (eventId) => {
        navigate(`/evento?id=${eventId}`);
    }

    const handleDeleteUser = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.delete(`/api/auth/delete`, {
                headers: { authorization: sessionStorage.getItem('token') },
                params: { userId: user._id }
            });

            if (response.status === 200) {
                alert("Account eliminato con successo.");
                sessionStorage.removeItem('token');
                navigate('/register');
            } else {
                console.error('Errore durante l\'eliminazione:', response);
                alert('Impossibile eliminare l\'account.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Errore nella richiesta di eliminazione.');
        }
    };

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div className='Profile d-flex flex-column min-vh-100 bg-dark text-white'>
            <Navbar />
            <div className="container flex-grow-1 my-5">
                <div className="row">
                    <div className='col-lg-3 col-sm-4 bg-dark'>
                        <div className='profile-info p-4 bg-light rounded-3 text-center'>
                            <h1 className='mb-4 text-warning fw-bolder'>{user.username}</h1>
                            <p className='mb-3 text-light'>Email: {user.email}</p>
                            <p className='mb-3 text-light'>Organizzatore: {user.organizer ? "Sì" : "No"}</p>
                            <p className='mb-3 text-light'>Numero eventi: {events.length}</p>

                            <button className='btn btn-danger mt-4' onClick={handleDeleteUser}>Elimina account</button>
                        </div>
                    </div>
                    <div className='col-lg-9 col-sm-8 bg-dark p-0'>
                        <div className='events-history p-4 bg-secondary rounded-3'>
                            <h1 className='text-light fw-bolder mb-4 text-center'>Storico eventi</h1>
                            <div className='row'>
                                {events.length > 0 ? (
                                    events.map((event) => {
                                        const data = new Date(event.date);
                                        return (
                                            <div key={event._id} className="event card col-lg-6 col-xxl-4 mb-4 border-0 bg-transparent text-start" onClick={() => handleEventClick(event._id)}>
                                                <figure className="card-img-top mb-0 overflow-hidden position-relative">
                                                    <img
                                                        src={event.images.length > 0 ? event.images[0] : '/assets/img/logoNuovo.png'}
                                                        alt={`event`}
                                                        className="img-thumbnail"
                                                        style={{ height: '200px', objectFit: 'cover' }}
                                                    />
                                                    <figcaption className="position-absolute top-0 start-0 p-2 text-white bg-dark bg-opacity-75 rounded-end">
                                                        <h6 className="m-0">Scopri evento</h6>
                                                    </figcaption>
                                                </figure>
                                                <div className="card-body p-3 bg-light">
                                                    <div className="entry-header row">
                                                        <ul className="entry-meta list-unstyled d-flex mb-2">
                                                            <li className='col-6'>
                                                                <span className="badge bg-warning text-dark text-uppercase fs-7">{event.category}</span>
                                                            </li>
                                                            <li className='col-6 text-end'>
                                                                <span className="text-muted">
                                                                    {data.toLocaleDateString('it-IT', {
                                                                        year: 'numeric',
                                                                        month: 'numeric',
                                                                        day: 'numeric'
                                                                    })}, {data.toLocaleTimeString('it-IT', {
                                                                        hour: '2-digit',
                                                                        minute: '2-digit'
                                                                    })}
                                                                </span>
                                                            </li>
                                                        </ul>
                                                    </div>
                                                    <h2 className="card-title entry-title h5 text-center text-warning">
                                                        {event.name}
                                                    </h2>
                                                </div>
                                            </div>
                                        );
                                    })
                                ) : (
                                    <div className='mt-3 text-muted'>Nessun evento passato</div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default Profile;
