import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Footer from './Footer';
import Navbar from './Navbar';
import './App.css';

function Organizer() {
    const [user, setUser] = useState(null);
    const [events, setEvents] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/homepage');
        } else {
            axios.get('/api/auth/verifyOrganizer', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/homepage');
                    } else {
                        setUser(response.data.user);
                        fetchUserEvents();
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
        }
    }, [navigate]);

    const handleEventClick = (eventId) => {
        const goto = '/evento?id=' + eventId;
        navigate(goto);
    };

    const fetchUserEvents = async () => {
        await axios.get('/api/events/eventsOf', { headers: { authorization: sessionStorage.getItem('token') } })
            .then(response => {
                setEvents(response.data.events || []);
            })
            .catch(error => {
                console.error('Error fetching events:', error);
            });
    };

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (
        <div className="Organizer fullpage">
            <Navbar />

            <div className="container my-5">
                <div className="row justify-content-center">
                    <div className="col-12 col-lg-10">
                        <div className="card border-light shadow-lg rounded-3">
                            <div className="card-body p-4">
                                <h1 className="display-5 fw-bold text-primary text-center mb-4">I tuoi eventi</h1>
                                {events.length > 0 ? (
                                    <div>
                                        <div className="text-center mb-5">
                                            <a className="btn btn-primary btn-lg" href="/organizzatore/crea">
                                                Crea
                                            </a>
                                        </div>
                                        <div className="row">
                                            {events.map((event) => {
                                                const data = new Date(event.date);
                                                return (
                                                    <div
                                                        key={event._id}
                                                        className="col-12 col-md-6 col-lg-4 mb-4"
                                                        onClick={() => handleEventClick(event._id)}
                                                    >
                                                        <div className="card h-100 border-0 bg-light shadow-sm rounded">
                                                            <figure className="mb-0 overflow-hidden">
                                                                <a>
                                                                    {event.images.length > 0 ? (
                                                                        <img
                                                                            src={`${event.images[0]}`}
                                                                            alt={`event`}
                                                                            className="card-img-top img-thumbnail rounded-0"
                                                                        />
                                                                    ) : (
                                                                        <img
                                                                            src="/assets/img/logoNuovo.png"
                                                                            alt={`event`}
                                                                            className="card-img-top img-thumbnail rounded-0"
                                                                        />
                                                                    )}
                                                                </a>
                                                                <figcaption className="text-center">
                                                                    <svg
                                                                        xmlns="http://www.w3.org/2000/svg"
                                                                        width="32"
                                                                        height="32"
                                                                        fill="currentColor"
                                                                        className="bi bi-eye text-primary mt-2"
                                                                        viewBox="0 0 16 16"
                                                                    >
                                                                        <path d="M16 8s-3-5.5-8-5.5S0 8 0 8s3 5.5 8 5.5S16 8 16 8zM1.173 8a13.133 13.133 0 0 1 1.66-2.043C4.12 4.668 5.88 3.5 8 3.5c2.12 0 3.879 1.168 5.168 2.457A13.133 13.133 0 0 1 14.828 8c-.058.087-.122.183-.195.288-.335.48-.83 1.12-1.465 1.755C11.879 11.332 10.119 12.5 8 12.5c-2.12 0-3.879-1.168-5.168-2.457A13.134 13.134 0 0 1 1.172 8z" />
                                                                        <path d="M8 5.5a2.5 2.5 0 1 0 0 5 2.5 2.5 0 0 0 0-5zM4.5 8a3.5 3.5 0 1 1 7 0 3.5 3.5 0 0 1-7 0z" />
                                                                    </svg>
                                                                    <h4 className="h6 text-primary mt-2">Scopri evento</h4>
                                                                </figcaption>
                                                            </figure>
                                                            <div className="card-body">
                                                                <div className="row mb-3">
                                                                    <div className="col-6">
                                                                        <span className="badge bg-secondary text-light">
                                                                            {event.category}
                                                                        </span>
                                                                    </div>
                                                                    <div className="col-6 text-end">
                                                                        <small className="text-muted">
                                                                            {data.toLocaleDateString('it-IT', {
                                                                                year: 'numeric',
                                                                                month: 'numeric',
                                                                                day: 'numeric',
                                                                            })}
                                                                            , {data.toLocaleTimeString('it-IT', {
                                                                                hour: '2-digit',
                                                                                minute: '2-digit',
                                                                            })}
                                                                        </small>
                                                                    </div>
                                                                </div>
                                                                <h5 className="card-title text-center">
                                                                    <a
                                                                        href="#!"
                                                                        className="link-primary text-decoration-none"
                                                                    >
                                                                        {event.name}
                                                                    </a>
                                                                </h5>
                                                            </div>
                                                        </div>
                                                    </div>
                                                );
                                            })}
                                        </div>
                                    </div>
                                ) : (
                                    <div className="text-center">
                                        <p className="text-secondary pt-5 pb-5">Nessun evento creato</p>
                                        <a className="btn btn-primary btn-lg" href="/organizzatore/crea">
                                            Crea
                                        </a>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <Footer />
        </div>
    );
}

export default Organizer;
