import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';
import { GoogleMap, LoadScript, Marker} from '@react-google-maps/api';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

// Sostituisci con il tuo ID mappa
const mapId = '6e674a5f15d5b512';

function Mappa() {
    const [markers, setMarkers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);  // Stato per il caricamento

    const fetchMarkers = async () => {
        try {
            const response = await axios.get('/api/events/markers');  // Sostituisci con l'endpoint corretto
            setMarkers(response.data);
            console.log('Fetched markers:', response.data);  // Verifica i dati dei marker
        } catch (error) {
            console.error('Error fetching marker data:', error);
        } finally {
            setIsLoading(false);  // Imposta loading a false una volta che i dati sono stati recuperati
        }
    };

    useEffect(() => {
        fetchMarkers();
    }, []);



    return (
        <div className="Mappa justify-content-center fullpage">
            <Navbar />
            <LoadScript
                    googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs"  // Sostituisci con la tua API Key
                    mapIds={[mapId]}  // Aggiungi l'ID della mappa se necessario
                >
            <div className='m-5 shadow map'>

                    {isLoading ? (
                        <div className="spinner-container">
                            CARICANDO {/* Spinner per indicare il caricamento */}
                        </div>
                    ) : (
                        <GoogleMap
                            mapContainerStyle={mapContainerStyle}
                            center={center}
                            zoom={15}
                        >
                            {markers.map((marker, index) => (
                                <Marker
                                    key={index}
                                    position={{ lat: marker.lat, lng: marker.lng }}  // Assicurati che le coordinate siano corrette
                                />
                            ))}
                        </GoogleMap>)}
            </div>
            </LoadScript>

            <Footer />
        </div>
    );
}

export default Mappa;
