import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const EventDetails = () => {
    const [event, setEvent] = useState(null);
    const [error, setError] = useState('');
    const location = useLocation();

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        if (eventId) {
            axios.get(`/api/events/${eventId}`, { headers: { authorization: localStorage.getItem('token') } })
                .then(response => {
                    setEvent(response.data);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    console.error(error);
                });
        } else {
            setError('Event ID not found');
        }
    }, [location]);

    if (error) {
        return <div>{error}</div>;
    }

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div className="fullpage">
            <Navbar />

            <div className=" container px-lg-5">
                <div className="futureEvents p-4 p-lg-5 bg-white rounded-3 text-center" >
                    <div className="m-4 m-lg-4">
                        <h1 className="display-5 fw-bold text-primary">I tuoi eventi furuti</h1>
                    </div>
                    <div className="event-details">
                        <h2>{event.title}</h2>
                        <p><strong>Description:</strong> {event.description}</p>
                        <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                        <p><strong>Location:</strong> {event.location}</p>
                        {/* Puoi aggiungere ulteriori dettagli dell'evento qui */}
                    </div>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default EventDetails;
