import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

// Sostituisci con il tuo ID mappa
const mapId = '6e674a5f15d5b512';

const {Map} = await google.maps.importLibrary("maps");

new Map(document.getElementById("map"), {
    zoom:15, 
    center: center,
    mapId: mapId
})

function Mappa() {
    const [markers, setMarkers] = useState([]);
    const mapRef = useRef(null);

    useEffect(() => {
        // Carica i dati dei marker dal tuo DB
        axios.get('/api/events/markers')  // Sostituisci con l'endpoint corretto
            .then(response => {
                setMarkers(response.data);
                console.log('Fetched markers:', response.data);  // Verifica i dati dei marker
            })
            .catch(error => {
                console.error('Error fetching marker data:', error);
            });
    }, []);


    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow map'>
                    <div id="map" style={mapContainerStyle}></div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Mappa;
