import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import Navbar from './Navbar';
import Footer from './Footer';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

function Map() {
    const [markerPosition, setMarkerPosition] = useState({
        lat: 37.51136,
        lng: 15.08709,
    });
    const [mapLoaded, setMapLoaded] = useState(false);

    useEffect(() => {
        if (mapLoaded) {
            console.log('Google Maps API is loaded');
        }
    }, [mapLoaded]);

    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow'>
                    <LoadScript
                        googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs"
                        onLoad={() => setMapLoaded(true)}
                        onError={(error) => console.error('Google Maps API load error:', error)}
                    >
                        <GoogleMap
                            mapContainerStyle={mapContainerStyle}
                            center={markerPosition}
                            zoom={15}
                        >
                            <Marker
                                position={markerPosition}
                                icon={{
                                    url: 'https://cdn-icons-png.freepik.com/512/2886/2886372.png',
                                    scaledSize: new window.google.maps.Size(50, 50),
                                }} 
                            />
                        </GoogleMap>
                    </LoadScript>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Map;
