import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const mapContainerStyle = {
    height: '650px',
    width: '100%',
};

const center = {
    lat: 37.51136,
    lng: 15.08709,
};

function Map() {
    const [markers, setMarkers] = useState([]);
    const mapRef = useRef(null);
    const mapElementRef = useRef(null);

    // useEffect(() => {
    //     // Carica i dati dei marker dal tuo DB
    //     axios.get('/api/events')  // Sostituisci con l'endpoint corretto
    //         .then(response => {
    //             setMarkers(response.data);  // Assicurati che i dati siano nella forma giusta
    //         })
    //         .catch(error => {
    //             console.error('Error fetching marker data:', error);
    //         });
    // }, []);

    useEffect(() => {
        if (mapElementRef.current) {
            mapRef.current = new google.maps.Map(mapElementRef.current, {
                center,
                zoom: 15,
                mapId: '6e674a5f15d5b512', // Sostituisci con il tuo Map ID
            });

            // Aggiungi i marker alla mappa
            markers.forEach(marker => {
                new google.maps.marker.AdvancedMarkerElement({
                    map: mapRef.current,
                    position: { lat: marker.lat, lng: marker.lng },
                    content: `<div style="color: red;">${marker.title}</div>`, // Personalizza il contenuto
                    // Puoi anche aggiungere altre opzioni qui come `icon` o `title`
                });
            });
        }
    }, [markers]);

    return (
        <div>
            <div className="Map justify-content-center fullpage">
                <Navbar />
                <div className='m-5 shadow map'>
                    <div ref={mapElementRef} style={mapContainerStyle} />
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Map;
