import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

import './App.css'

function Organizer() {

    const [user, setUser] = useState(null);

    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, navigate);

    const [formData, setFormData] = useState({ name: '', description : '', date: null, category: 0,  });
    const [errorMessage, setErrorMessage] = useState('');

    //Cambia il contenuto di formData in tempo reale mentre si scrive
    const handleChange = (e) => {
      const { name, value, type, checked } = e.target;
      setFormData((prevData) => ({
        ...prevData,
        [name]: type === 'checkbox' ? checked : value,
      }));
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
          const response = await axios.post('/', formData);
          localStorage.setItem('token', response.data.token); // Salva il token
          navigate('/homepage');
      } catch (error) {
          console.error('Error:', error);
          setErrorMessage('Invalid login credentials.');
      } 
    };

    if(!user){
        return <div>Caricamento in corso...</div>
    }

    return (

        <div className="Organizer ">

            <nav className="navbar navbar-expand-lg bg-success shadow-lg fixed-top">

                <div className="container-fluid">
                    <a className="navbar-brand text-white" href="/homepage">
                        <img src="assets/img/logoNuovo.png" alt="Eventi Logo" className="d-inline-block align-text-top" style={{ width: '50px', height: '50px' }} />
                    </a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"> </span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <a className="nav-link text-white" aria-current="page" href="/homepage">Home</a>
                            </li>

                            <li className="nav-item dropdown">
                                <a className="nav-link dropdown-toggle text-white" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                    Categorie
                                </a>
                                <ul className="dropdown-menu text-white">
                                    <li><a className="dropdown-item" href="/categorie/cultura">Cultura</a></li>
                                    <li><a className="dropdown-item" href="/categorie/concerti">Concerti</a></li>
                                    {/* <li><hr className="dropdown-divider" /></li> */}
                                    <li><a className="dropdown-item" href="/categorie/mostre">Mostre</a></li>
                                    <li><a className="dropdown-item" href="/categorie/party">Party</a></li>
                                    <li><a className="dropdown-item" href="/categorie/sport">Sport</a></li>
                                </ul>
                            </li>

                            <li className="nav-item">
                                <a className="nav-link text-white" href="/mappa">Mappa</a>
                            </li>

                            <li className="nav-item">
                                <a className="nav-link text-white" href="/chat">Chat</a>
                            </li>

                            <li className="nav-item">
                                <a className="nav-link text-white" href="/profilo">Profilo</a>
                            </li>

                            <li className="nav-item">
                                <a className={`nav-link ${user && user.organizer ? 'text-white active' : 'disabled'}`} href={`${user && user.organizer ? '/organizzatore' : ''}`} aria-disabled={user && !user.organizer}>Organizzatore</a>
                            </li>
                        </ul>
                        <form className="d-flex" role="search">
                            <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                            <button className="btn btn-outline-light" type="submit">Search</button>
                        </form>
                    </div>
                </div>
            </nav>

            {/* ------------------------------------ SPAZIO SU --------------------------------------------- */}

            <div className="m-5 p-2">
                <div></div>
            </div>

            {/* ------------------------------------ FORM CREA EVENTO --------------------------------------------- */}

            <form onSubmit={handleSubmit}>
                  <div className="row gy-2 overflow-hidden">
                    <div className="col-12">
                      <div className="form-floating mb-3">
                        <input
                          type="email"
                          className="form-control border-primary"
                          name="emailIn"
                          id="emailIn"
                          placeholder="name@example.com"
                          value={formData.emailIn}
                          onChange={handleChange}
                          required
                        />
                        <label htmlFor="emailIn" className="form-label text-primary">
                          Email
                        </label>
                      </div>
                    </div>
                    <div className="col-12">
                      <div className="form-floating mb-3">
                        <input
                          type="password"
                          className="form-control border-primary"
                          name="passwordIn"
                          id="passwordIn"
                          placeholder="password"
                          value={formData.passwordIn}
                          onChange={handleChange}
                          required
                        />
                        <label htmlFor="passwordIn" className="form-label text-primary">
                          Passowrd
                        </label>
                      </div>
                    </div>
                    {errorMessage && (
                      <div className="col-12">
                        <p className="text-danger text-center">{errorMessage}</p>
                      </div>
                    )}

                    <div className="col-12">
                      <div className="d-grid my-3">
                        <button className="btn btn-primary btn-lg" type="submit">
                          Entra
                        </button>
                      </div>
                    </div>
                    <div className="col-12">
                      <p className="m-0 text-dark text-center">
                        Non hai ancora un account? <a href="./register" className="link-danger text-decoration-none text-dark">Registrati ora!</a>
                      </p>
                    </div>
                  </div>
                </form>




        </div>


    );
}

export default Organizer;
