import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import Navbar from './Navbar';
import Footer from './Footer';

function Map() {
    const [user, setUser] = useState(null);
    const [markerPosition, setMarkerPosition] = useState({
        lat: 37.51136,
        lng: 15.08709,
    });

    const navigate = useNavigate();
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/mappa');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/mappa');
                    } else {
                        setUser(response.data.user);
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/mappa');
                });
        }
    }, [navigate]);

    if (!user) {
        return <div>Caricamento in corso...</div>;
    }

    return (

        <div>
        <div className="Map justify-content-center">
            <Navbar />
            <div className='m-5 shadow'>
                <LoadScript googleMapsApiKey="AIzaSyDfHYlA0EODV9YfoDGc5pAO93XLqhu_0Fs">
                    <GoogleMap
                        mapContainerStyle={`height: '600px',
                            width: '100%'`}
                        zoom={15}
                    >
                        <Marker position={markerPosition} />
                    </GoogleMap>
                </LoadScript>
            </div>
        </div>
    <Footer />

    </div>
);


}

export default Map;
