import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, NavLink } from 'react-router-dom';
import './App.css'

function Navbar() {
    const [user, setUser] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = sessionStorage.getItem('token');
        if (!token) {
            navigate('/login');
        } else {
            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/login');
                    } else {
                        setUser(response.data.user); // Imposta l'utente
                    }
                })
                .catch((error) => {
                    console.error('Verification error:', error);
                    navigate('/login');
                });
        }
    }, [navigate]);

    const logout = () => {
        sessionStorage.removeItem('token');
        navigate('/login');
    }

    const handleSearch = (event) => {
        event.preventDefault();
        axios.get('/api/events/search', { params: { q: searchTerm } })
            .then(response => {
                setSearchResults(response.data);
            })
            .catch(error => {
                console.error('Search error:', error);
            });
    };

    const handleInputChange = (event) => {
        setSearchTerm(event.target.value);
    };

    if (!user) {
        return <div>Caricamento in corso...</div>
    }

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-success fixed-top shadow">
                <div className="container-fluid">
                    <a className="navbar-brand" href="/homepage">
                        <img src="/assets/img/logoNuovo.png" alt="" className="d-inline-block align-text-top" style={{ width: '50px', height: '50px' }} />
                    </a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            {/* Restante codice di navigazione */}
                        </ul>
                        <form className="d-flex" role="search" onSubmit={handleSearch}>
                            <input
                                className="form-control me-2"
                                type="search"
                                placeholder="Search"
                                aria-label="Search"
                                value={searchTerm}
                                onChange={handleInputChange}
                            />
                            <button className="btn btn-outline-light me-2" type="submit">Search</button>
                        </form>
                        <ul className='navbar-nav mb-2 mb-lg-0 mt-lg-0 mt-3 align-items-center'>
                            <li className='nav-item'>
                                <a onClick={() => logout()} className='btn btn-danger text-white ms-lg-5'>
                                    LogOut
                                </a>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>

            <div className="m-5 p-2">
                {searchResults.length > 0 && (
                    <div>
                        <h3>Risultati della ricerca:</h3>
                        <ul>
                            {searchResults.map(event => (
                                <li key={event._id}>
                                    <a href={`/event/${event._id}`}>{event.name}</a>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Navbar;
