import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { useNavigate, useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';
import { Carousel } from 'mdb-ui-kit'; // Importa solo i componenti JS senza CSS

// Inizializza MDB
import { initMDB } from 'mdb-ui-kit';
initMDB({ Carousel });

const serverUrl = window.location.hostname === 'localhost'
    ? 'http://localhost:5000'
    : 'http://192.168.1.25:5000';

const serverBaseUrl = 'http://localhost:5000';

const socket = io(serverUrl);

const EventDetails = () => {
    const token = sessionStorage.getItem('token');
    const [event, setEvent] = useState(null);
    const [user, setUser] = useState(null);
    const [isPartecipating, setIsPartecipating] = useState(false);
    const [comments, setComments] = useState([]);
    const [error, setError] = useState('');
    const [formData, setFormData] = useState({
        text: '',
    });
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        if (eventId) {
            axios.get(`/api/events/${eventId}`, { headers: { authorization: token } })
                .then(response => {
                    setEvent(response.data);
                    fetchEventComments(eventId);
                })
                .catch(error => {
                    setError('Failed to load event details');
                    console.error(error);
                });

            axios.get('/api/auth/verify', { headers: { authorization: token } })
                .then(response => {
                    if (response.status !== 200) {
                        navigate('/homepage');
                    } else {
                        setUser(response.data.user);
                        fetchPartecipationStatus(response.data.user._id);
                    }
                })
                .catch(error => {
                    console.error('Verification error:', error);
                    navigate('/homepage');
                });
        } else {
            setError('Event ID not found');
        }

        socket.on('newComment', (savedComment) => {
            console.log("Received new comment: ", savedComment);
            setComments(prevComments => {
                const updatedComments = [...prevComments, savedComment];
                updatedComments.sort((a, b) => new Date(b.date) - new Date(a.date));
                return updatedComments;
            });
        });

        return () => {
            socket.off('newComment');
        };
    }, [location, token, navigate]);

    const fetchPartecipationStatus = async (userId) => {
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        try {
            console.log(userId, eventId);
            const response = await axios.get('/api/partecipate/check', {
                params: {
                    eventId: eventId,
                    userId: userId
                }
            });

            console.log("Partecipa?", response.data.partecipa);
            setIsPartecipating(response.data.partecipa);
        } catch (error) {
            console.error('Errore nel recupero dello stato di partecipazione:', error);
        }
    };

    const fetchEventComments = async (eventId) => {
        try {
            const response = await axios.get('/api/events/commentsOf', { headers: { event: eventId } });
            const comments = response.data.comments || [];
            comments.sort((a, b) => new Date(b.date) - new Date(a.date));
            setComments(comments);
        } catch (error) {
            setError('Failed to load comments');
            console.error(error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handlePartecipate = async (e) => {
        e.preventDefault();
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        try {
            const response = await axios.post(`/api/partecipate`,
                {
                    dati: {
                        eventId: eventId,
                        userId: user._id
                    }
                }, { headers: { authorization: token } });

            if (response.status === 200) {
                fetchPartecipationStatus(user._id);
                fetchPartecipationStatus(user._id);
                fetchPartecipationStatus(user._id);
            } else {
                console.error('Errore durante la partecipazione:', response);
                alert('Impossibile confermare la partecipazione.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Errore nella richiesta di partecipazione.');
        }
    };

    const handleNotPartecipate = async (e) => {
        e.preventDefault();
        const queryParams = new URLSearchParams(location.search);
        const eventId = queryParams.get('id');

        try {
            const response = await axios.post(`/api/partecipate/stop`,
                {
                    dati: {
                        eventId: eventId,
                        userId: user._id
                    }
                });

            if (response.status === 200) {
                fetchPartecipationStatus(user._id);
            } else {
                console.error('Errore durante la non partecipazione:', response);
                alert('Impossibile confermare la non partecipazione.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Errore nella richiesta di non partecipazione.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const queryParams = new URLSearchParams(location.search);
        const comment = {
            text: formData.text,
            user: user._id,
            event: queryParams.get('id'),
            data: new Date()
        };

        try {
            const response = await axios.post('/api/events/addComment', comment);
            console.log(response);
            if (response.status === 201) {
                socket.emit('sendComment', response.data);
                setFormData({ text: '' });
            } else {
                console.error('Error adding comment:', response);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    if (error) {
        return <div>{error}</div>;
    }

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <Navbar />
            <div className="container px-lg-5">
                <div className="futureEvents p-4 p-lg-5 bg-white rounded-3 text-center">
                    <div className="m-4 m-lg-4 row">
                        <a className='col-lg-2 col-2 text-decoration-none' name="partecipa" id="partecipa" href="javascript:history.back()">
                            <button type="button" className="btn d-flex align-items-center flex-nowrap">
                                <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" fill="currentColor" className="bi text-dark bi-arrow-return-left" viewBox="0 0 16 16">
                                    <path fillRule="evenodd" d="M14.5 1.5a.5.5 0 0 1 .5.5v4.8a2.5 2.5 0 0 1-2.5 2.5H2.707l3.347 3.346a.5.5 0 0 1-.708.708l-4.2-4.2a.5.5 0 0 1 0-.708l4-4a.5.5 0 1 1 .708.708L2.707 8.3H12.5A1.5 1.5 0 0 0 14 6.8V2a.5.5 0 0 1 .5-.5" />
                                </svg>
                            </button>
                        </a>
                        <h1 className="display-5 fw-bold text-primary col-lg-8 col-8">{event.name}</h1>
                        {isPartecipating ? (
                            <a className='col-lg-2 col-2 text-decoration-none' name="partecipa" id="partecipa" onClick={handleNotPartecipate}>
                                <button type="button" className="btn btn-secondary d-flex align-items-center flex-nowrap">
                                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-heart me-2" viewBox="0 0 16 16">
                                        <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.288 3.197 3.286 4.095a.56.56 0 0 0 .773 0c.99-.904 2.376-2.295 3.306-4.122.955-1.852.878-3.27.324-4.293C9.656 2.604 8.758 2.749 8 2.748z" />
                                    </svg>
                                    Partecipa
                                </button>
                            </a>
                        ) : (
                            <a className='col-lg-2 col-2 text-decoration-none' name="partecipa" id="partecipa" onClick={handlePartecipate}>
                                <button type="button" className="btn btn-outline-primary d-flex align-items-center flex-nowrap">
                                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-heart me-2" viewBox="0 0 16 16">
                                        <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.288 3.197 3.286 4.095a.56.56 0 0 0 .773 0c.99-.904 2.376-2.295 3.306-4.122.955-1.852.878-3.27.324-4.293C9.656 2.604 8.758 2.749 8 2.748z" />
                                    </svg>
                                    Partecipa
                                </button>
                            </a>
                        )}
                    </div>
                    <Carousel>
                        {event.images.map((image, index) => (
                            <Carousel.Item >
                                <img src={image} className="d-block w-100" alt={`Slide ${index}`} />
                            </Carousel.Item>
                        ))}
                    </Carousel>
                    <div className="row">
                        <h4 className="text-center mt-4 mb-4">Commenti</h4>
                        <div className="col-12 d-flex justify-content-center mb-3">
                            <form className="w-50" onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <textarea
                                        className="form-control"
                                        id="exampleFormControlTextarea1"
                                        rows="2"
                                        placeholder="Scrivi un commento..."
                                        name="text"
                                        value={formData.text}
                                        onChange={handleChange}
                                    />
                                </div>
                                <button type="submit" className="btn btn-primary">Invia</button>
                            </form>
                        </div>
                        <div className="container mt-4">
                            {comments.length === 0 ? (
                                <p className="text-center">Nessun commento ancora</p>
                            ) : (
                                comments.map(comment => (
                                    <div key={comment._id} className="card mb-2">
                                        <div className="card-body">
                                            <p className="card-text">{comment.text}</p>
                                            <footer className="blockquote-footer">
                                                {comment.user.username} <cite title="Source Title">{new Date(comment.date).toLocaleDateString()}</cite>
                                            </footer>
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default EventDetails;
