import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'
import './App.css'

function Organizer() {

  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/homepage');
    } else {
      axios.get('/api/auth/verifyOrganizer', { headers: { authorization: token } })
        .then(response => {
          if (response.status !== 200) {
            navigate('/homepage');
          } else {
            setUser(response.data.user);
            fetchUserEvents(); // Imposta l'utente
          }
        })
        .catch((error) => {
          console.error('Verification error:', error);
          navigate('/homepage');
        });
    }
  }, [navigate]);


  const fetchUserEvents = () => {
    axios.get('/api/events/eventsOf', { headers: { authorization: localStorage.getItem('token') } })
      .then(response => {
        setEvents(response.data.events || []);
      })
      .catch(error => {
        console.error('Error fetching events:', error);
      });
  };

  if (!user) {
    return <div>Caricamento in corso...</div>
  }

  return (

    <div className="Organizer fullpage">

      <Navbar />

      <div className='container px-lg-5 p-4 p-lg-5 bg-white rounded-3 text-center'>
      <h1 className="display-5 fw-bold text-primary text-center">I tuoi eventi</h1>
      {events.length > 0 ? (<div>
        <button className="btn btn-primary btn-lg" type="submit">
          Crea
        </button>
        <ul>
          {events.map(event => (
            <li key={event._id}>{event.name} - {event.date}</li>
          ))}
        </ul>
      </div>
      ) : (
        <div className='container'>
          <p className='text-secondary text-center pt-5 pb-5'>Nessun evento creato</p>
          <a className="btn btn-primary btn-lg center" href='/organizzatore/crea'>
            Crea
          </a>
        </div>
      )}
      </div>




      {/* ------------------------------------ FORM CREA EVENTO --------------------------------------------- 

       */}
    </div>


  );
}

export default Organizer;
